using Microsoft.AspNetCore.Authentication.JwtBearer;
using Microsoft.IdentityModel.Tokens;
using System.Text;
using UEM.Satellite.API.Data;
using UEM.Satellite.API.Data.Repositories;
using UEM.Satellite.API.Services;
using UEM.Satellite.API.Store;
using Serilog;

var builder = WebApplication.CreateBuilder(args);

// Configure Serilog
builder.Host.UseSerilog((context, configuration) =>
{
    configuration
        .ReadFrom.Configuration(context.Configuration)
        .WriteTo.Console()
        .WriteTo.File("logs/satellite-api-.log", rollingInterval: RollingInterval.Day)
        .Enrich.FromLogContext();
});

// Add services to the container.
builder.Services.AddControllers();
builder.Services.AddEndpointsApiExplorer();
builder.Services.AddSwaggerGen(c =>
{
    c.SwaggerDoc("v1", new() { 
        Title = "UEM Satellite API", 
        Version = "v1",
        Description = "Enterprise-grade Unified Endpoint Management Satellite API"
    });
    c.AddSecurityDefinition("Bearer", new Microsoft.OpenApi.Models.OpenApiSecurityScheme
    {
        Description = "JWT Authorization header using the Bearer scheme",
        Name = "Authorization",
        In = Microsoft.OpenApi.Models.ParameterLocation.Header,
        Type = Microsoft.OpenApi.Models.SecuritySchemeType.ApiKey
    });
});

// Database configuration
var connectionString = builder.Configuration.GetConnectionString("Postgres") 
    ?? Environment.GetEnvironmentVariable("DATABASE_URL") 
    ?? throw new InvalidOperationException("Database connection string not found");

// Database repositories with Dapper
builder.Services.AddScoped<IAgentRepository, AgentRepository>();
builder.Services.AddScoped<IHardwareRepository, HardwareRepository>();
builder.Services.AddScoped<ISoftwareRepository, SoftwareRepository>();
builder.Services.AddScoped<IProcessRepository, ProcessRepository>();
builder.Services.AddScoped<INetworkRepository, NetworkRepository>();
builder.Services.AddScoped<IEnhancedHeartbeatRepository, EnhancedHeartbeatRepository>();

// Agent simulation service for testing
builder.Services.AddHostedService<UEM.Satellite.API.Services.AgentSimulationService>();

// Dapper factory for legacy repository compatibility
builder.Services.AddSingleton<IDbFactory>(provider => new DbFactory(builder.Configuration));

// Authentication
builder.Services.AddAuthentication(JwtBearerDefaults.AuthenticationScheme)
    .AddJwtBearer(options =>
    {
        options.TokenValidationParameters = new TokenValidationParameters
        {
            ValidateIssuerSigningKey = true,
            IssuerSigningKey = new SymmetricSecurityKey(
                Encoding.UTF8.GetBytes(builder.Configuration["Jwt:SecretKey"] ?? "your-super-secret-jwt-key-here")),
            ValidateIssuer = false,
            ValidateAudience = false
        };
    });

// CORS for UI
builder.Services.AddCors(opt => {
    opt.AddPolicy("AllowAll", p => p
        .AllowAnyOrigin()
        .AllowAnyHeader()
        .AllowAnyMethod());
});

// Register services
builder.Services.AddScoped<AgentRegistry>();
builder.Services.AddScoped<TokenService>();
builder.Services.AddScoped<HeartbeatRepository>();
builder.Services.AddScoped<AgentStore>();
builder.Services.AddScoped<DiscoveryScriptPopulationService>();

// Policy deployment services
builder.Services.AddScoped<IPolicyDeploymentService, PolicyDeploymentService>();
builder.Services.AddScoped<IAgentStatusService, AgentStatusService>();

// Health checks
builder.Services.AddHealthChecks();

var app = builder.Build();

// Configure the HTTP request pipeline.
if (app.Environment.IsDevelopment())
{
    app.UseSwagger();
    app.UseSwaggerUI(c => 
    {
        c.SwaggerEndpoint("/swagger/v1/swagger.json", "UEM Satellite API v1");
        c.RoutePrefix = "swagger";
    });
}

app.UseSerilogRequestLogging();
app.UseCors("AllowAll");
app.UseAuthentication();
app.UseAuthorization();

// Serve static files from built frontend
app.UseStaticFiles(new StaticFileOptions
{
    FileProvider = new Microsoft.Extensions.FileProviders.PhysicalFileProvider(
        Path.Combine(Directory.GetCurrentDirectory(), "../UEM.WebApp/dist/public")),
    RequestPath = ""
});

app.MapControllers();
app.MapHealthChecks("/health");

// Serve index.html for SPA routes (everything that's not API or static files)
app.MapFallback(async (HttpContext context) =>
{
    var indexPath = Path.Combine(Directory.GetCurrentDirectory(), "../UEM.WebApp/dist/public/index.html");
    if (File.Exists(indexPath))
    {
        context.Response.ContentType = "text/html";
        await context.Response.SendFileAsync(indexPath);
    }
    else
    {
        context.Response.StatusCode = 404;
        await context.Response.WriteAsync("Frontend files not found. Please build the frontend first.");
    }
});

app.MapGet("/api/status", () => new { 
    status = "healthy", 
    timestamp = DateTime.UtcNow,
    version = "v1.0.0",
    environment = app.Environment.EnvironmentName
});

// Additional Swagger endpoints
app.MapGet("/swagger", () => Results.Redirect("/swagger/index.html"));

// Initialize database tables through repositories if needed
try
{
    using var scope = app.Services.CreateScope();
    var agentRepo = scope.ServiceProvider.GetRequiredService<IAgentRepository>();
    // Tables will be created on first use
    var logger = scope.ServiceProvider.GetRequiredService<ILogger<Program>>();
    logger.LogInformation("Database repositories initialized successfully");
}
catch (Exception ex)
{
    var logger = app.Services.GetRequiredService<ILogger<Program>>();
    logger.LogWarning(ex, "Could not initialize database repositories, continuing with fallback storage");
}

// Configure port from settings
var port = builder.Configuration.GetValue<int>("ServerSettings:Port", 8000);
app.Run($"http://0.0.0.0:{port}");

UEM.ScriptExecLib/ScriptExecFacade.cs.content:
namespace ScriptExecLib;
using ScriptExecLib.Models;
using ScriptExecLib.Services;

public enum ScriptType
{
    PowerShell,
    Batch,
    Python,
    Shell,
    Wmi,
    Registry,
    FileSystem
}

public sealed class ScriptExecFacade
{
    private readonly PowerShellExecutor _ps = new();
    private readonly ShellExecutor _sh = new();
    private readonly PythonExecutor _py = new();
    private readonly WmiQueryService _wmi = new();
    private readonly RegistryQueryService _reg = new();
    private readonly FileSystemScanner _fs = new();
    private readonly WindowsBatchExecutor _bat = new();

    public async Task<object?> ExecuteAsync(ScriptType type, object request, CancellationToken ct = default)
    {
        return type switch
        {
            ScriptType.PowerShell => await _ps.ExecuteAsync((ExecRequest)request, ct),
            ScriptType.Batch => await _bat.ExecuteAsync((ExecRequest)request, ct),
            ScriptType.Python => await _py.ExecuteAsync((ExecRequest)request, ct),
            ScriptType.Shell => await _sh.ExecuteAsync((ExecRequest)request, ct),
            ScriptType.Wmi => await _wmi.QueryAsync((WmiQueryRequest)request, ct),
            ScriptType.Registry => _reg.QueryAsJson((RegistryQueryOptions)request),
            ScriptType.FileSystem => await _fs.ScanAsync((FileScanOptions)request, ct),
            _ => throw new NotSupportedException($"Script type {type} is not supported.")
        };
    }

    public Task<Models.ExecResult> RunPowerShellAsync(ExecRequest req, CancellationToken ct = default) => _ps.ExecuteAsync(req, ct);
    public Task<Models.ExecResult> RunShellAsync(ExecRequest req, CancellationToken ct = default) => _sh.ExecuteAsync(req, ct);
    public Task<Models.ExecResult> RunPythonAsync(ExecRequest req, CancellationToken ct = default) => _py.ExecuteAsync(req, ct);
    public Task<string> RunWmiAsync(WmiQueryRequest req, CancellationToken ct = default) => _wmi.QueryAsync(req, ct);
    public string QueryRegistryAsJson(RegistryQueryOptions options) => _reg.QueryAsJson(options);
    public Task<string> ScanFileSystemAsync(FileScanOptions options, CancellationToken ct = default) => _fs.ScanAsync(options, ct);
    public Task<ExecResult> RunBatchAsync(ExecRequest req, CancellationToken ct = default) => _bat.ExecuteAsync(req, ct);
}

