import React, { useState, useEffect, useMemo } from "react";
import { Link, useLocation } from "wouter";
import {
  ChevronDown,
  ChevronRight,
  Search,
  X,
  Building2,
  Zap,
  ChevronLeft,
  Menu,
  Loader2
} from "lucide-react";
