      action: 'extract',
      target: 'system_info',
      enabled: true,
      priority: 1
    },
    {
      id: 2,
      name: 'Extract Hardware Information',
      type: 'json_path', 
      pattern: '$.Data.Hardware',
      action: 'extract',
      target: 'hardware_info',
      enabled: true,
      priority: 2
    },
    {
      id: 3,
      name: 'Process Memory Values',
