  ]);

  // Enterprise features state
  const [approvalWorkflow, setApprovalWorkflow] = useState({
    enabled: false,
    approvers: [] as string[],
    requiresTwoApprovals: false
  });

  const [versionControl, setVersionControl] = useState({
    enabled: true,
    autoIncrement: true,
    changeDescription: ''
  });

  const [auditSettings, setAuditSettings] = useState({
    enabled: true,
    logExecutions: true,
    logChanges: true
