import React, { useState } from "react";
import { Link, useLocation } from "wouter";
import {
  Shield,
  LayoutDashboard,
  Monitor,
  Search,
  Users,
  Bell,
  BarChart3,
  Settings,
  ChevronDown,
  Circle,
  FileText,
  Code,
  Radar,
  ShoppingCart,
  ArrowRightLeft,
  Globe,
  Building2,
  Database,
  Zap,
} from "lucide-react";
import { useLanguage } from "@/contexts/LanguageContext";
import { cn } from "@/lib/utils";

interface SidebarProps {
  isOpen: boolean;
  onClose: () => void;
}

