}

interface AssetField {
  id: string;
  name: string;
  type: 'text' | 'number' | 'date' | 'select' | 'multiselect' | 'boolean' | 'currency';
  required: boolean;
  defaultValue?: any;
  options?: string[];
  validation?: {
    min?: number;
    max?: number;
    pattern?: string;
  };
  category: 'basic' | 'location' | 'business' | 'technical' | 'financial' | 'compliance';
}

// API Response Types
interface HardwareComponent {
  id: string;
  componentType: string;
  manufacturer: string;
  model: string;
  serialNumber?: string;
  version?: string;
  capacity?: number;
  properties?: Record<string, any>;
  discoveredAt: string;
  updatedAt: string;
}

interface SoftwareItem {
  id: string;
  name: string;
  version?: string;
  publisher?: string;
  installLocation?: string;
  sizeBytes?: number;
  installDate?: string;
  softwareType: string;
  licenseKey?: string;
  discoveredAt: string;
  updatedAt: string;
}

interface NetworkInterface {
  id: string;
  interfaceName: string;
  description?: string;
  macAddress?: string;
  ipAddress?: string;
  subnetMask?: string;
  gateway?: string;
  dnsServers?: string[];
  isActive: boolean;
  interfaceType: string;
  bytesSent: number;
  bytesReceived: number;
  speed: number;
  timestamp: string;
}

interface HeartbeatData {
  id: string;
  agentId: string;
  cpuUsage: number;
  memoryUsedBytes: number;
  memoryTotalBytes: number;
  diskUsedBytes: number;
  diskTotalBytes: number;
  processCount: number;
  networkConnectionCount: number;
  uptimeHours: number;
  timestamp: string;
}

interface AssetDetailsDialogProps {
  asset: Asset;
  customFields: AssetField[];
}

export function AssetDetailsDialog({ asset, customFields }: AssetDetailsDialogProps) {
  const [activeTab, setActiveTab] = useState('overview');

  const getCriticalityColor = (criticality: string) => {
    switch (criticality) {
      case 'critical': return 'bg-red-100 text-red-800 border-red-200';
      case 'high': return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800 border-green-200';
      case 'inactive': return 'bg-gray-100 text-gray-800 border-gray-200';
      case 'maintenance': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'decommissioned': return 'bg-red-100 text-red-800 border-red-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const renderCustomFieldValue = (field: AssetField, value: any) => {
    if (!value) return '-';

    switch (field.type) {
      case 'boolean':
        return value ? 'Yes' : 'No';
      case 'currency':
        return `$${value.toLocaleString()}`;
      case 'date':
        return new Date(value).toLocaleDateString();
      case 'multiselect':
        return Array.isArray(value) ? value.join(', ') : value;
      default:
        return value.toString();
    }
  };

  const customFieldsByCategory = customFields.reduce((acc, field) => {
