                      <div className="space-y-3 max-h-96 overflow-y-auto">
                        {softwareData.map((software, index) => (
                          <div key={index} className="border rounded-lg p-4 hover:bg-gray-50">
                            <div className="flex items-start justify-between">
                              <div className="flex-1">
                                <div className="flex items-center space-x-3">
                                  <h4 className="font-medium text-lg">{software.name}</h4>
                                  <Badge variant="outline" className="text-xs">
                                    {software.softwareType}
                                  </Badge>
                                  {software.licenseKey && (
                                    <Badge variant="secondary" className="text-xs">
                                      Licensed
                                    </Badge>
                                  )}
                                </div>
                                <div className="grid grid-cols-2 md:grid-cols-3 gap-4 mt-3 text-sm">
                                  <div>
                                    <span className="font-medium text-gray-500">Version:</span>
                                    <span className="ml-2">{software.version || 'Unknown'}</span>
                                  </div>
                                  <div>
                                    <span className="font-medium text-gray-500">Publisher:</span>
                                    <span className="ml-2">{software.publisher || 'Unknown'}</span>
                                  </div>
                                  {software.sizeBytes && (
                                    <div>
                                      <span className="font-medium text-gray-500">Size:</span>
                                      <span className="ml-2">
                                        {software.sizeBytes > 1024 * 1024 * 1024 
                                          ? `${(software.sizeBytes / (1024 * 1024 * 1024)).toFixed(2)} GB`
                                          : `${(software.sizeBytes / (1024 * 1024)).toFixed(2)} MB`}
                                      </span>
                                    </div>
                                  )}
                                  {software.installDate && (
                                    <div>
                                      <span className="font-medium text-gray-500">Installed:</span>
                                      <span className="ml-2">{new Date(software.installDate).toLocaleDateString()}</span>
                                    </div>
                                  )}
                                  {software.installLocation && (
                                    <div className="md:col-span-2">
                                      <span className="font-medium text-gray-500">Location:</span>
                                      <span className="ml-2 font-mono text-xs">{software.installLocation}</span>
                                    </div>
                                  )}
                                </div>
                                <div className="mt-2 text-xs text-gray-400">
                                  Discovered: {new Date(software.discoveredAt).toLocaleString()}
                                </div>
                              </div>
                            </div>
                          </div>
                        ))}
                      </div>
                    </CardContent>
                  </Card>
                </>
              ) : (
                <Card>
                  <CardContent className="text-center py-8">
                    <Package className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <p className="text-gray-500">No software inventory discovered</p>
                    <p className="text-sm text-gray-400 mt-2">Software discovery may not be configured for this endpoint</p>
                  </CardContent>
                </Card>
              )}
            </div>
          )}
        </TabsContent>

        {/* Security Tab */}
        <TabsContent value="security" className="space-y-6">
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            <Card>
              <CardHeader>
                <CardTitle>Vulnerabilities</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-3">
                  {securityInfo.vulnerabilities.map((vuln, index) => (
                    <div key={index} className="border rounded-lg p-3">
                      <div className="flex items-center justify-between mb-2">
                        <span className="font-medium">{vuln.id}</span>
                        <Badge className={vuln.severity === 'High' ? getCriticalityColor('critical') : 
                                        vuln.severity === 'Medium' ? getCriticalityColor('medium') : 
                                        getCriticalityColor('low')}>
                          {vuln.severity}
                        </Badge>
                      </div>
                      <p className="text-sm text-gray-600">{vuln.description}</p>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>

            <Card>
              <CardHeader>
                <CardTitle>Security Status</CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                <div className="flex items-center justify-between">
                  <span>Antivirus Status</span>
                  <Badge className="bg-green-100 text-green-800">{securityInfo.antivirusStatus}</Badge>
                </div>
                <div className="flex items-center justify-between">
                  <span>Firewall Status</span>
                  <Badge className="bg-green-100 text-green-800">{securityInfo.firewallStatus}</Badge>
                </div>
                <Separator />
                <div>
                  <h4 className="font-medium mb-2">Patch Status</h4>
                  <div className="space-y-2">
                    {securityInfo.patches.map((patch, index) => (
                      <div key={index} className="flex items-center justify-between text-sm">
                        <span>{patch.id}</span>
                        <div className="flex items-center space-x-2">
                          <span className="text-gray-500">{patch.date}</span>
                          <Badge className={patch.installed ? 'bg-green-100 text-green-800' : 'bg-yellow-100 text-yellow-800'}>
                            {patch.installed ? 'Installed' : 'Pending'}
                          </Badge>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </CardContent>
            </Card>
          </div>
        </TabsContent>

        {/* Custom Fields Tab */}
        <TabsContent value="custom" className="space-y-6">
          {Object.entries(customFieldsByCategory).map(([category, fields]) => (
            <Card key={category}>
              <CardHeader>
                <CardTitle className="capitalize">{category.replace('_', ' ')} Fields</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  {fields.map((field) => (
                    <div key={field.id}>
                      <p className="text-sm font-medium text-gray-500">{field.name}</p>
                      <p className="font-medium">
                        {renderCustomFieldValue(field, asset.customFields[field.id])}
                      </p>
                    </div>
                  ))}
                </div>
              </CardContent>
            </Card>
          ))}
        </TabsContent>

        {/* Audit Log Tab */}
        <TabsContent value="audit" className="space-y-6">
          <Card>
            <CardHeader>
              <CardTitle className="flex items-center space-x-2">
                <History className="h-5 w-5" />
                <span>Asset Audit Log</span>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {auditLog.map((entry, index) => (
                  <div key={index} className="flex items-start space-x-4 pb-4 border-b last:border-b-0">
                    <div className="flex-shrink-0 w-2 h-2 bg-blue-500 rounded-full mt-2"></div>
                    <div className="flex-1">
                      <div className="flex items-center justify-between">
                        <p className="font-medium">{entry.action}</p>
                        <span className="text-sm text-gray-500">{entry.timestamp}</span>
                      </div>
                      <p className="text-sm text-gray-600 mt-1">{entry.details}</p>
                      <p className="text-xs text-gray-500 mt-1">by {entry.user}</p>
                    </div>
                  </div>
                ))}
              </div>
            </CardContent>
          </Card>
        </TabsContent>
      </Tabs>
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Checkbox } from '@/components/ui/checkbox';
import { Textarea } from '@/components/ui/textarea';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
  DialogFooter,
} from '@/components/ui/dialog';
import { 
  Plus,
  Trash2,
  Edit,
  Save,
  Type,
  Hash,
  Calendar,
  List,
  ToggleLeft,
  DollarSign,
  Settings,
  ArrowUp,
  ArrowDown
} from 'lucide-react';
import { DragDropContext, Droppable, Draggable } from 'react-beautiful-dnd';

interface AssetField {
  id: string;
  name: string;
  type: 'text' | 'number' | 'date' | 'select' | 'multiselect' | 'boolean' | 'currency';
  required: boolean;
  defaultValue?: any;
  options?: string[];
  validation?: {
    min?: number;
    max?: number;
    pattern?: string;
  };
  category: 'basic' | 'location' | 'business' | 'technical' | 'financial' | 'compliance';
  displayOrder: number;
  description?: string;
  placeholder?: string;
}

interface AssetFormBuilderProps {
  customFields: AssetField[];
}

export function AssetFormBuilder({ customFields }: AssetFormBuilderProps) {
  const [fields, setFields] = useState<AssetField[]>(customFields);
  const [selectedField, setSelectedField] = useState<AssetField | null>(null);
  const [isCreateMode, setIsCreateMode] = useState(false);
  const [showFieldDialog, setShowFieldDialog] = useState(false);
  const [previewMode, setPreviewMode] = useState(false);

  // Field form state
  const [fieldName, setFieldName] = useState('');
  const [fieldType, setFieldType] = useState<AssetField['type']>('text');
  const [fieldCategory, setFieldCategory] = useState<AssetField['category']>('basic');
  const [fieldRequired, setFieldRequired] = useState(false);
  const [fieldDescription, setFieldDescription] = useState('');
  const [fieldPlaceholder, setFieldPlaceholder] = useState('');
  const [fieldDefaultValue, setFieldDefaultValue] = useState('');
  const [fieldOptions, setFieldOptions] = useState<string[]>(['']);
  const [fieldValidation, setFieldValidation] = useState({
    min: '',
    max: '',
    pattern: ''
  });

  useEffect(() => {
    setFields(customFields);
  }, [customFields]);

  const fieldCategories = [
    { value: 'basic', label: 'Basic Information', icon: Type },
    { value: 'location', label: 'Location & Geography', icon: Hash },
    { value: 'business', label: 'Business Context', icon: Settings },
    { value: 'technical', label: 'Technical Details', icon: Settings },
    { value: 'financial', label: 'Financial Information', icon: DollarSign },
    { value: 'compliance', label: 'Compliance & Security', icon: Settings },
  ];

  const fieldTypes = [
    { value: 'text', label: 'Text', icon: Type, description: 'Single line text input' },
    { value: 'number', label: 'Number', icon: Hash, description: 'Numeric input with validation' },
    { value: 'date', label: 'Date', icon: Calendar, description: 'Date picker' },
    { value: 'select', label: 'Dropdown', icon: List, description: 'Single selection from options' },
    { value: 'multiselect', label: 'Multi-Select', icon: List, description: 'Multiple selections from options' },
    { value: 'boolean', label: 'Toggle', icon: ToggleLeft, description: 'True/false toggle' },
    { value: 'currency', label: 'Currency', icon: DollarSign, description: 'Monetary value input' },
  ];

  const handleCreateField = () => {
    setIsCreateMode(true);
    setSelectedField(null);
    resetFieldForm();
    setShowFieldDialog(true);
  };

  const handleEditField = (field: AssetField) => {
    setIsCreateMode(false);
    setSelectedField(field);
    setFieldName(field.name);
    setFieldType(field.type);
    setFieldCategory(field.category);
    setFieldRequired(field.required);
    setFieldDescription(field.description || '');
    setFieldPlaceholder(field.placeholder || '');
    setFieldDefaultValue(field.defaultValue || '');
    setFieldOptions(field.options || ['']);
    setFieldValidation({
      min: field.validation?.min?.toString() || '',
      max: field.validation?.max?.toString() || '',
      pattern: field.validation?.pattern || ''
    });
    setShowFieldDialog(true);
  };

  const resetFieldForm = () => {
    setFieldName('');
    setFieldType('text');
    setFieldCategory('basic');
    setFieldRequired(false);
    setFieldDescription('');
    setFieldPlaceholder('');
    setFieldDefaultValue('');
    setFieldOptions(['']);
    setFieldValidation({ min: '', max: '', pattern: '' });
  };

  const handleSaveField = () => {
    const newField: AssetField = {
      id: isCreateMode ? `custom_${Date.now()}` : selectedField!.id,
      name: fieldName,
      type: fieldType,
      category: fieldCategory,
      required: fieldRequired,
      description: fieldDescription,
      placeholder: fieldPlaceholder,
      defaultValue: fieldDefaultValue,
      options: (fieldType === 'select' || fieldType === 'multiselect') ? fieldOptions.filter(opt => opt.trim()) : undefined,
      validation: {
        min: fieldValidation.min ? parseInt(fieldValidation.min) : undefined,
        max: fieldValidation.max ? parseInt(fieldValidation.max) : undefined,
        pattern: fieldValidation.pattern || undefined,
      },
      displayOrder: isCreateMode ? fields.length : selectedField!.displayOrder,
    };

    if (isCreateMode) {
      setFields(prev => [...prev, newField]);
    } else {
      setFields(prev => prev.map(f => f.id === selectedField!.id ? newField : f));
    }

    setShowFieldDialog(false);
  };

  const handleDeleteField = (fieldId: string) => {
    setFields(prev => prev.filter(f => f.id !== fieldId));
  };

  const handleDragEnd = (result: any) => {
    if (!result.destination) return;

    const items = Array.from(fields);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    // Update display order
    const updatedItems = items.map((item, index) => ({
      ...item,
      displayOrder: index
    }));

    setFields(updatedItems);
  };

  const addOption = () => {
    setFieldOptions(prev => [...prev, '']);
  };

  const updateOption = (index: number, value: string) => {
    setFieldOptions(prev => prev.map((opt, i) => i === index ? value : opt));
  };

  const removeOption = (index: number) => {
    setFieldOptions(prev => prev.filter((_, i) => i !== index));
  };

  const getFieldIcon = (type: string) => {
    const fieldType = fieldTypes.find(ft => ft.value === type);
    return fieldType?.icon || Type;
  };

  const renderFieldPreview = (field: AssetField) => {
    const commonProps = {
      id: field.id,
      placeholder: field.placeholder,
      required: field.required,
      className: "w-full"
    };

    switch (field.type) {
      case 'text':
        return <Input {...commonProps} defaultValue={field.defaultValue} />;
      case 'number':
        return <Input {...commonProps} type="number" defaultValue={field.defaultValue} />;
      case 'date':
        return <Input {...commonProps} type="date" defaultValue={field.defaultValue} />;
      case 'select':
        return (
          <Select defaultValue={field.defaultValue}>
            <SelectTrigger>
              <SelectValue placeholder={field.placeholder} />
            </SelectTrigger>
            <SelectContent>
              {field.options?.map((option, index) => (
                <SelectItem key={index} value={option}>{option}</SelectItem>
              ))}
            </SelectContent>
          </Select>
        );
      case 'multiselect':
        return (
          <div className="space-y-2">
            {field.options?.map((option, index) => (
              <div key={index} className="flex items-center space-x-2">
                <Checkbox id={`${field.id}_${index}`} />
                <Label htmlFor={`${field.id}_${index}`}>{option}</Label>
              </div>
            ))}
          </div>
        );
      case 'boolean':
        return (
          <div className="flex items-center space-x-2">
            <Checkbox id={field.id} defaultChecked={field.defaultValue} />
            <Label htmlFor={field.id}>{field.name}</Label>
          </div>
        );
      case 'currency':
        return (
          <div className="relative">
            <DollarSign className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
            <Input {...commonProps} type="number" className="pl-10" defaultValue={field.defaultValue} />
          </div>
        );
      default:
        return <Input {...commonProps} defaultValue={field.defaultValue} />;
    }
  };

  const fieldsByCategory = fields.reduce((acc, field) => {
    if (!acc[field.category]) {
      acc[field.category] = [];
    }
    acc[field.category].push(field);
    return acc;
  }, {} as Record<string, AssetField[]>);

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold">Asset Form Builder</h2>
          <p className="text-gray-600">Design dynamic forms with custom fields for asset management</p>
        </div>
        <div className="flex items-center space-x-2">
          <Button 
            variant="outline" 
            onClick={() => setPreviewMode(!previewMode)}
          >
            {previewMode ? 'Edit Mode' : 'Preview Mode'}
          </Button>
          <Button onClick={handleCreateField}>
            <Plus className="h-4 w-4 mr-2" />
            Add Custom Field
          </Button>
        </div>
      </div>

      {previewMode ? (
        /* Form Preview Mode */
        <Card>
          <CardHeader>
            <CardTitle>Asset Form Preview</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-6">
              {fieldCategories.map((category) => {
                const categoryFields = fieldsByCategory[category.value] || [];
                if (categoryFields.length === 0) return null;

                return (
                  <div key={category.value}>
                    <h3 className="text-lg font-semibold mb-4 flex items-center space-x-2">
                      <category.icon className="h-5 w-5" />
                      <span>{category.label}</span>
                    </h3>
                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                      {categoryFields
                        .sort((a, b) => a.displayOrder - b.displayOrder)
                        .map((field) => (
                          <div key={field.id} className="space-y-2">
                            <Label htmlFor={field.id} className="flex items-center space-x-2">
                              <span>{field.name}</span>
                              {field.required && <span className="text-red-500">*</span>}
                            </Label>
                            {field.description && (
                              <p className="text-sm text-gray-500">{field.description}</p>
                            )}
                            {renderFieldPreview(field)}
                          </div>
                        ))}
                    </div>
                  </div>
                );
              })}
            </div>
          </CardContent>
        </Card>
      ) : (
        /* Field Management Mode */
        <div className="space-y-6">
          {/* Field Statistics */}
          <div className="grid grid-cols-2 md:grid-cols-6 gap-4">
            {fieldCategories.map((category) => {
              const count = fieldsByCategory[category.value]?.length || 0;
              return (
                <Card key={category.value}>
                  <CardContent className="p-4">
                    <div className="flex items-center space-x-2">
                      <category.icon className="h-4 w-4 text-blue-500" />
                      <div>
                        <p className="text-2xl font-bold">{count}</p>
                        <p className="text-xs text-gray-500">{category.label}</p>
                      </div>
                    </div>
                  </CardContent>
                </Card>
              );
            })}
          </div>

          {/* Field List */}
          <Card>
            <CardHeader>
              <CardTitle>Custom Fields ({fields.length})</CardTitle>
            </CardHeader>
            <CardContent>
              <DragDropContext onDragEnd={handleDragEnd}>
                <Droppable droppableId="fields">
                  {(provided) => (
                    <div {...provided.droppableProps} ref={provided.innerRef} className="space-y-2">
                      {fields
                        .sort((a, b) => a.displayOrder - b.displayOrder)
                        .map((field, index) => {
                          const FieldIcon = getFieldIcon(field.type);
                          return (
                            <Draggable key={field.id} draggableId={field.id} index={index}>
                              {(provided, snapshot) => (
                                <div
                                  ref={provided.innerRef}
                                  {...provided.draggableProps}
                                  {...provided.dragHandleProps}
                                  className={`p-4 border rounded-lg bg-white ${
                                    snapshot.isDragging ? 'shadow-lg' : ''
                                  }`}
                                >
                                  <div className="flex items-center justify-between">
                                    <div className="flex items-center space-x-3">
                                      <FieldIcon className="h-5 w-5 text-gray-400" />
                                      <div>
                                        <h4 className="font-medium">{field.name}</h4>
                                        <div className="flex items-center space-x-2 mt-1">
                                          <Badge variant="outline" className="text-xs">
                                            {fieldTypes.find(ft => ft.value === field.type)?.label}
                                          </Badge>
                                          <Badge variant="outline" className="text-xs">
                                            {fieldCategories.find(fc => fc.value === field.category)?.label}
                                          </Badge>
                                          {field.required && (
                                            <Badge variant="destructive" className="text-xs">
                                              Required
                                            </Badge>
                                          )}
                                        </div>
                                        {field.description && (
                                          <p className="text-sm text-gray-500 mt-1">{field.description}</p>
                                        )}
                                      </div>
                                    </div>
                                    <div className="flex items-center space-x-2">
                                      <Button
                                        variant="ghost"
                                        size="sm"
                                        onClick={() => handleEditField(field)}
                                      >
                                        <Edit className="h-4 w-4" />
                                      </Button>
                                      <Button
                                        variant="ghost"
                                        size="sm"
                                        onClick={() => handleDeleteField(field.id)}
                                      >
                                        <Trash2 className="h-4 w-4" />
                                      </Button>
                                    </div>
                                  </div>
                                </div>
                              )}
                            </Draggable>
                          );
                        })}
                      {provided.placeholder}
                    </div>
                  )}
                </Droppable>
              </DragDropContext>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Field Designer Dialog */}
      <Dialog open={showFieldDialog} onOpenChange={setShowFieldDialog}>
        <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>
              {isCreateMode ? 'Create Custom Field' : `Edit Field: ${selectedField?.name}`}
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            {/* Basic Information */}
            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="fieldName">Field Name *</Label>
                <Input
                  id="fieldName"
                  value={fieldName}
                  onChange={(e) => setFieldName(e.target.value)}
                  placeholder="Enter field name"
                />
              </div>
              <div>
                <Label htmlFor="fieldType">Field Type *</Label>
                <Select value={fieldType} onValueChange={(value: AssetField['type']) => setFieldType(value)}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    {fieldTypes.map((type) => (
                      <SelectItem key={type.value} value={type.value}>
                        <div className="flex items-center space-x-2">
                          <type.icon className="h-4 w-4" />
                          <div>
                            <div>{type.label}</div>
                            <div className="text-xs text-gray-500">{type.description}</div>
                          </div>
                        </div>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>
            </div>

            <div>
              <Label htmlFor="fieldCategory">Category *</Label>
              <Select value={fieldCategory} onValueChange={(value: AssetField['category']) => setFieldCategory(value)}>
                <SelectTrigger>
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {fieldCategories.map((category) => (
                    <SelectItem key={category.value} value={category.value}>
                      <div className="flex items-center space-x-2">
                        <category.icon className="h-4 w-4" />
                        <span>{category.label}</span>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>

            <div>
              <Label htmlFor="fieldDescription">Description</Label>
              <Textarea
                id="fieldDescription"
                value={fieldDescription}
                onChange={(e) => setFieldDescription(e.target.value)}
                placeholder="Describe this field's purpose"
                rows={2}
              />
            </div>

            <div className="grid grid-cols-2 gap-4">
              <div>
                <Label htmlFor="fieldPlaceholder">Placeholder Text</Label>
                <Input
                  id="fieldPlaceholder"
                  value={fieldPlaceholder}
                  onChange={(e) => setFieldPlaceholder(e.target.value)}
                  placeholder="Enter placeholder text"
                />
              </div>
              <div>
                <Label htmlFor="fieldDefaultValue">Default Value</Label>
                <Input
                  id="fieldDefaultValue"
                  value={fieldDefaultValue}
                  onChange={(e) => setFieldDefaultValue(e.target.value)}
                  placeholder="Enter default value"
                />
              </div>
            </div>

            {/* Options for select/multiselect */}
            {(fieldType === 'select' || fieldType === 'multiselect') && (
              <div>
                <Label>Options</Label>
                <div className="space-y-2 mt-2">
                  {fieldOptions.map((option, index) => (
                    <div key={index} className="flex items-center space-x-2">
                      <Input
                        value={option}
                        onChange={(e) => updateOption(index, e.target.value)}
                        placeholder={`Option ${index + 1}`}
                      />
                      <Button
                        variant="ghost"
                        size="sm"
                        onClick={() => removeOption(index)}
                        disabled={fieldOptions.length === 1}
                      >
                        <Trash2 className="h-4 w-4" />
                      </Button>
                    </div>
                  ))}
                  <Button variant="outline" size="sm" onClick={addOption}>
                    <Plus className="h-4 w-4 mr-2" />
                    Add Option
                  </Button>
                </div>
              </div>
            )}

            {/* Validation Rules */}
            {(fieldType === 'text' || fieldType === 'number') && (
              <div>
                <Label>Validation Rules</Label>
                <div className="grid grid-cols-3 gap-2 mt-2">
                  <div>
                    <Label htmlFor="minValue" className="text-xs">Min Value/Length</Label>
                    <Input
                      id="minValue"
                      type="number"
                      value={fieldValidation.min}
                      onChange={(e) => setFieldValidation(prev => ({ ...prev, min: e.target.value }))}
                      placeholder="Min"
                    />
                  </div>
                  <div>
                    <Label htmlFor="maxValue" className="text-xs">Max Value/Length</Label>
                    <Input
                      id="maxValue"
                      type="number"
                      value={fieldValidation.max}
                      onChange={(e) => setFieldValidation(prev => ({ ...prev, max: e.target.value }))}
                      placeholder="Max"
                    />
                  </div>
                  <div>
                    <Label htmlFor="pattern" className="text-xs">Regex Pattern</Label>
                    <Input
                      id="pattern"
                      value={fieldValidation.pattern}
                      onChange={(e) => setFieldValidation(prev => ({ ...prev, pattern: e.target.value }))}
                      placeholder="Regex"
                    />
                  </div>
                </div>
              </div>
            )}

            <div className="flex items-center space-x-2">
              <Checkbox 
                id="fieldRequired" 
                checked={fieldRequired} 
                onCheckedChange={(checked) => setFieldRequired(!!checked)} 
              />
              <Label htmlFor="fieldRequired">Required field</Label>
            </div>
          </div>

          <DialogFooter>
            <Button variant="outline" onClick={() => setShowFieldDialog(false)}>
              Cancel
            </Button>
            <Button onClick={handleSaveField} disabled={!fieldName}>
              <Save className="h-4 w-4 mr-2" />
              {isCreateMode ? 'Create Field' : 'Update Field'}
            </Button>
          </DialogFooter>
        </DialogContent>
      </Dialog>
    </div>
  );
}

import React, { useState, useMemo } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { 
  Building2,
  MapPin,
  Briefcase,
  Users,
  ChevronDown,
  ChevronRight,
  Search,
  TreePine,
  Network,
  Layers
} from 'lucide-react';

interface Asset {
  id: number;
  name: string;
  ipAddress: string;
  status: 'active' | 'inactive' | 'maintenance' | 'decommissioned';
  criticality: 'critical' | 'high' | 'medium' | 'low';
  location?: string;
  businessUnit?: string;
  project?: string;
  reportingManager?: string;
  category?: string;
}

interface AssetHierarchyViewProps {
  assets: Asset[];
}

interface HierarchyNode {
  id: string;
  label: string;
  type: 'root' | 'location' | 'businessUnit' | 'project' | 'manager' | 'asset';
  assets: Asset[];
  children: HierarchyNode[];
  expanded: boolean;
  level: number;
}

export function AssetHierarchyView({ assets }: AssetHierarchyViewProps) {
  const [hierarchyType, setHierarchyType] = useState<'location' | 'business' | 'network'>('location');
  const [expandedNodes, setExpandedNodes] = useState<Set<string>>(new Set());
  const [searchQuery, setSearchQuery] = useState('');

  const hierarchyOptions = [
    { value: 'location', label: 'Location Hierarchy', icon: MapPin },
    { value: 'business', label: 'Business Hierarchy', icon: Briefcase },
    { value: 'network', label: 'Network Hierarchy', icon: Network },
  ];

  const buildLocationHierarchy = (assets: Asset[]): HierarchyNode[] => {
    const locationGroups = assets.reduce((acc, asset) => {
      const location = asset.location || 'Unassigned';
      if (!acc[location]) {
        acc[location] = [];
      }
      acc[location].push(asset);
      return acc;
    }, {} as Record<string, Asset[]>);

    return Object.entries(locationGroups).map(([location, locationAssets]) => ({
      id: `location-${location}`,
      label: location,
      type: 'location' as const,
      assets: locationAssets,
      children: locationAssets.map(asset => ({
        id: `asset-${asset.id}`,
        label: asset.name,
        type: 'asset' as const,
        assets: [asset],
        children: [],
        expanded: false,
        level: 2,
      })),
      expanded: expandedNodes.has(`location-${location}`),
      level: 1,
    }));
  };

  const buildBusinessHierarchy = (assets: Asset[]): HierarchyNode[] => {
    const businessGroups = assets.reduce((acc, asset) => {
      const businessUnit = asset.businessUnit || 'Unassigned';
      const project = asset.project || 'No Project';
      const manager = asset.reportingManager || 'No Manager';

      if (!acc[businessUnit]) {
        acc[businessUnit] = {};
      }
      if (!acc[businessUnit][project]) {
        acc[businessUnit][project] = {};
      }
      if (!acc[businessUnit][project][manager]) {
        acc[businessUnit][project][manager] = [];
      }
      acc[businessUnit][project][manager].push(asset);
      return acc;
    }, {} as Record<string, Record<string, Record<string, Asset[]>>>);

    return Object.entries(businessGroups).map(([businessUnit, projects]) => ({
      id: `bu-${businessUnit}`,
      label: businessUnit,
      type: 'businessUnit' as const,
      assets: Object.values(projects).flatMap(p => Object.values(p).flat()),
      children: Object.entries(projects).map(([project, managers]) => ({
        id: `project-${businessUnit}-${project}`,
        label: project,
        type: 'project' as const,
        assets: Object.values(managers).flat(),
        children: Object.entries(managers).map(([manager, managerAssets]) => ({
          id: `manager-${businessUnit}-${project}-${manager}`,
          label: manager,
          type: 'manager' as const,
          assets: managerAssets,
          children: managerAssets.map(asset => ({
            id: `asset-${asset.id}`,
            label: asset.name,
            type: 'asset' as const,
            assets: [asset],
            children: [],
            expanded: false,
            level: 4,
          })),
          expanded: expandedNodes.has(`manager-${businessUnit}-${project}-${manager}`),
          level: 3,
        })),
        expanded: expandedNodes.has(`project-${businessUnit}-${project}`),
        level: 2,
      })),
      expanded: expandedNodes.has(`bu-${businessUnit}`),
      level: 1,
    }));
  };

  const buildNetworkHierarchy = (assets: Asset[]): HierarchyNode[] => {
    const networkGroups = assets.reduce((acc, asset) => {
      // Extract network segment from IP address (first 3 octets)
      const networkSegment = asset.ipAddress.split('.').slice(0, 3).join('.') + '.0/24';
      if (!acc[networkSegment]) {
        acc[networkSegment] = [];
      }
      acc[networkSegment].push(asset);
      return acc;
    }, {} as Record<string, Asset[]>);

    return Object.entries(networkGroups).map(([network, networkAssets]) => ({
      id: `network-${network}`,
      label: network,
      type: 'root' as const,
      assets: networkAssets,
      children: networkAssets.map(asset => ({
        id: `asset-${asset.id}`,
        label: `${asset.name} (${asset.ipAddress})`,
        type: 'asset' as const,
        assets: [asset],
        children: [],
        expanded: false,
        level: 2,
      })),
      expanded: expandedNodes.has(`network-${network}`),
      level: 1,
    }));
  };

  const hierarchy = useMemo(() => {
    const filteredAssets = assets.filter(asset => 
      !searchQuery || 
      asset.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      asset.ipAddress.includes(searchQuery) ||
      asset.location?.toLowerCase().includes(searchQuery.toLowerCase()) ||
      asset.businessUnit?.toLowerCase().includes(searchQuery.toLowerCase())
    );

    switch (hierarchyType) {
      case 'location':
        return buildLocationHierarchy(filteredAssets);
      case 'business':
        return buildBusinessHierarchy(filteredAssets);
      case 'network':
        return buildNetworkHierarchy(filteredAssets);
      default:
        return [];
    }
  }, [assets, hierarchyType, searchQuery, expandedNodes]);

  const toggleNode = (nodeId: string) => {
    setExpandedNodes(prev => {
      const newSet = new Set(prev);
      if (newSet.has(nodeId)) {
        newSet.delete(nodeId);
      } else {
        newSet.add(nodeId);
      }
      return newSet;
    });
  };

  const expandAll = () => {
    const allNodeIds = new Set<string>();
    const collectNodeIds = (nodes: HierarchyNode[]) => {
      nodes.forEach(node => {
        allNodeIds.add(node.id);
        collectNodeIds(node.children);
      });
    };
    collectNodeIds(hierarchy);
    setExpandedNodes(allNodeIds);
  };

  const collapseAll = () => {
    setExpandedNodes(new Set());
  };

  const getCriticalityColor = (criticality: string) => {
    switch (criticality) {
      case 'critical': return 'bg-red-100 text-red-800 border-red-200';
      case 'high': return 'bg-orange-100 text-orange-800 border-orange-200';
      case 'medium': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'low': return 'bg-green-100 text-green-800 border-green-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800 border-green-200';
      case 'inactive': return 'bg-gray-100 text-gray-800 border-gray-200';
      case 'maintenance': return 'bg-yellow-100 text-yellow-800 border-yellow-200';
      case 'decommissioned': return 'bg-red-100 text-red-800 border-red-200';
      default: return 'bg-gray-100 text-gray-800 border-gray-200';
    }
  };

  const getNodeIcon = (type: string) => {
    switch (type) {
      case 'location': return MapPin;
      case 'businessUnit': return Building2;
      case 'project': return Briefcase;
      case 'manager': return Users;
      case 'asset': return TreePine;
      default: return Network;
    }
  };

  const renderNode = (node: HierarchyNode) => {
    const NodeIcon = getNodeIcon(node.type);
    const hasChildren = node.children.length > 0;
    const isExpanded = node.expanded;

    const paddingLeft = node.level * 20;

    return (
      <div key={node.id}>
        <div 
          className="flex items-center space-x-2 p-2 hover:bg-gray-50 rounded cursor-pointer"
          style={{ paddingLeft }}
          onClick={() => hasChildren && toggleNode(node.id)}
        >
          {hasChildren && (
            <Button variant="ghost" size="sm" className="p-0 h-4 w-4">
              {isExpanded ? <ChevronDown className="h-3 w-3" /> : <ChevronRight className="h-3 w-3" />}
            </Button>
          )}
          {!hasChildren && <div className="w-4" />}

          <NodeIcon className="h-4 w-4 text-gray-500" />

          <span className="font-medium">{node.label}</span>

          {node.type !== 'asset' && (
            <Badge variant="outline" className="text-xs">
              {node.assets.length} asset{node.assets.length !== 1 ? 's' : ''}
            </Badge>
          )}

          {node.type === 'asset' && node.assets[0] && (
            <div className="flex items-center space-x-2 ml-auto">
              <Badge className={getStatusColor(node.assets[0].status)} >
                {node.assets[0].status}
              </Badge>
              <Badge className={getCriticalityColor(node.assets[0].criticality)}>
                {node.assets[0].criticality}
              </Badge>
              <span className="text-sm text-gray-500 font-mono">
                {node.assets[0].ipAddress}
              </span>
            </div>
          )}
        </div>

        {isExpanded && node.children.map(child => renderNode(child))}
      </div>
    );
  };

  const getHierarchyStats = () => {
    const stats = {
      totalNodes: 0,
      totalAssets: assets.length,
      activeAssets: assets.filter(a => a.status === 'active').length,
      criticalAssets: assets.filter(a => a.criticality === 'critical').length,
    };

    const countNodes = (nodes: HierarchyNode[]) => {
      nodes.forEach(node => {
        stats.totalNodes++;
        countNodes(node.children);
      });
    };
    countNodes(hierarchy);

    return stats;
  };

  const stats = getHierarchyStats();

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold">Asset Hierarchy View</h2>
          <p className="text-gray-600">Visualize assets in hierarchical organizational structure</p>
        </div>
        <div className="flex items-center space-x-2">
          <Button variant="outline" size="sm" onClick={expandAll}>
            Expand All
          </Button>
          <Button variant="outline" size="sm" onClick={collapseAll}>
            Collapse All
          </Button>
        </div>
      </div>

      {/* Statistics */}
      <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Layers className="h-4 w-4 text-blue-500" />
              <div>
                <p className="text-2xl font-bold">{stats.totalNodes}</p>
                <p className="text-xs text-gray-500">Hierarchy Nodes</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <Building2 className="h-4 w-4 text-green-500" />
              <div>
                <p className="text-2xl font-bold">{stats.totalAssets}</p>
                <p className="text-xs text-gray-500">Total Assets</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <TreePine className="h-4 w-4 text-green-600" />
              <div>
                <p className="text-2xl font-bold">{stats.activeAssets}</p>
                <p className="text-xs text-gray-500">Active Assets</p>
              </div>
            </div>
          </CardContent>
        </Card>

        <Card>
          <CardContent className="p-4">
            <div className="flex items-center space-x-2">
              <TreePine className="h-4 w-4 text-red-500" />
              <div>
                <p className="text-2xl font-bold">{stats.criticalAssets}</p>
                <p className="text-xs text-gray-500">Critical Assets</p>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Controls */}
      <Card>
        <CardContent className="p-4">
          <div className="flex flex-col lg:flex-row gap-4">
            <div className="flex-1">
              <div className="relative">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-gray-400" />
                <Input
                  placeholder="Search assets, locations, or business units..."
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>

            <div>
              <Select value={hierarchyType} onValueChange={(value: 'location' | 'business' | 'network') => setHierarchyType(value)}>
                <SelectTrigger className="w-64">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  {hierarchyOptions.map((option) => (
                    <SelectItem key={option.value} value={option.value}>
                      <div className="flex items-center space-x-2">
                        <option.icon className="h-4 w-4" />
                        <span>{option.label}</span>
                      </div>
                    </SelectItem>
                  ))}
                </SelectContent>
              </Select>
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Hierarchy Tree */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center space-x-2">
            {(() => {
              const option = hierarchyOptions.find(opt => opt.value === hierarchyType);
              if (option?.icon) {
                const IconComponent = option.icon;
                return <IconComponent className="h-5 w-5" />;
              }
              return null;
            })()}
            <span>{hierarchyOptions.find(opt => opt.value === hierarchyType)?.label}</span>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="max-h-96 overflow-y-auto">
            {hierarchy.length > 0 ? (
              <div className="space-y-1">
                {hierarchy.map(node => renderNode(node))}
              </div>
            ) : (
              <div className="text-center py-8 text-gray-500">
                <TreePine className="h-12 w-12 mx-auto mb-3 text-gray-300" />
                <p>No assets found matching the current filters</p>
              </div>
            )}
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

