  --enterprise-gray-dark: hsl(215, 16%, 87%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }

  /* Enterprise-specific styles */
  .enterprise-shadow {
    box-shadow: 0 1px 3px 0 rgba(0, 0, 0, 0.1), 0 1px 2px 0 rgba(0, 0, 0, 0.06);
  }

  .enterprise-shadow-lg {
    box-shadow: 0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05);
  }

  /* Smooth transitions for theme switching */
  .transition-colors {
    transition-property: color, background-color, border-color, text-decoration-color, fill, stroke;
    transition-timing-function: cubic-bezier(0.4, 0, 0.2, 1);
    transition-duration: 300ms;
  }

  /* Custom scrollbar for modern look */
  .custom-scrollbar::-webkit-scrollbar {
    width: 6px;
  }

  .custom-scrollbar::-webkit-scrollbar-track {
    background: hsl(var(--muted));
  }

  .custom-scrollbar::-webkit-scrollbar-thumb {
    background: hsl(var(--muted-foreground));
    border-radius: 3px;
  }

  .custom-scrollbar::-webkit-scrollbar-thumb:hover {
    background: hsl(var(--foreground));
  }
}

/* Loading animation for enterprise feel */
@keyframes enterprise-pulse {
  0%, 100% {
    opacity: 1;
  }
  50% {
    opacity: 0.5;
  }
}

.enterprise-pulse {
  animation: enterprise-pulse 2s cubic-bezier(0.4, 0, 0.6, 1) infinite;
}

/* Status indicator styles */
.status-online {
  @apply bg-green-500;
  animation: pulse 2s infinite;
}

.status-warning {
  @apply bg-yellow-500;
  animation: pulse 2s infinite;
}

.status-offline {
  @apply bg-red-500;
}

/* Responsive design enhancements */
@media (max-width: 768px) {
  .mobile-stack {
    @apply flex-col space-y-4 space-x-0;
  }
}

