          <CardContent>
            <Select
              value={formData.probeId?.toString() || 'default'}
              onValueChange={(value) => updateFormData('probeId', value === 'default' ? null : parseInt(value))}
            >
              <SelectTrigger>
                <SelectValue placeholder="Select satellite server (optional)" />
              </SelectTrigger>
              <SelectContent>
                <SelectItem value="default">Use Default Server</SelectItem>
                {probes.map((probe: DiscoveryProbe) => (
                  <SelectItem key={probe.id} value={probe.id.toString()}>
                    <div className="flex items-center space-x-2">
                      <Server className="w-4 h-4" />
                      <span>{probe.name}</span>
                      <Badge variant="outline" className="ml-2">
                        {probe.status}
                      </Badge>
                    </div>
                  </SelectItem>
                ))}
              </SelectContent>
            </Select>
            <p className="text-xs text-gray-500 mt-2">
              Default server will be used if none selected
            </p>
          </CardContent>
        </Card>
      </div>
    </div>
  );

  const renderStep4 = () => (
    <Card>
      <CardHeader>
        <CardTitle>Schedule Configuration</CardTitle>
      </CardHeader>
      <CardContent className="space-y-6">
        <div>
          <Label className="text-base font-medium">Execution Time</Label>
          <div className="flex items-center space-x-4 mt-2">
            <label className="flex items-center space-x-2">
              <input
                type="radio"
                name="scheduleType"
                value="now"
                checked={formData.scheduleType === 'now'}
                onChange={(e) => updateFormData('scheduleType', e.target.value as 'now' | 'later')}
              />
              <span>Run Now</span>
            </label>
            <label className="flex items-center space-x-2">
              <input
                type="radio"
                name="scheduleType"
                value="later"
                checked={formData.scheduleType === 'later'}
                onChange={(e) => updateFormData('scheduleType', e.target.value as 'now' | 'later')}
              />
              <span>Schedule for Later</span>
            </label>
          </div>
        </div>

        {formData.scheduleType === 'later' && (
          <div className="space-y-4">
            <div>
              <Label>Schedule Type</Label>
              <Select
                value={formData.schedule.type}
                onValueChange={(value) => updateNestedFormData('schedule', 'type', value)}
              >
                <SelectTrigger className="mt-1">
                  <SelectValue />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="once">Run Once</SelectItem>
                  <SelectItem value="recurring">Recurring</SelectItem>
                </SelectContent>
              </Select>
            </div>

            {formData.schedule.type === 'recurring' && (
              <div>
                <Label>Frequency</Label>
                <Select
                  value={formData.schedule.frequency}
                  onValueChange={(value) => updateNestedFormData('schedule', 'frequency', value)}
                >
                  <SelectTrigger className="mt-1">
                    <SelectValue placeholder="Select frequency" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="daily">Daily</SelectItem>
                    <SelectItem value="weekly">Weekly</SelectItem>
                    <SelectItem value="monthly">Monthly</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            )}

            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <div>
                <Label>Time</Label>
                <Input
                  type="time"
                  value={formData.schedule.time}
                  onChange={(e) => updateNestedFormData('schedule', 'time', e.target.value)}
                  className="mt-1"
                />
              </div>
              <div>
                <Label>Timezone</Label>
                <Select
                  value={formData.schedule.timezone}
                  onValueChange={(value) => updateNestedFormData('schedule', 'timezone', value)}
                >
                  <SelectTrigger className="mt-1">
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="UTC">UTC</SelectItem>
                    <SelectItem value="America/New_York">Eastern Time</SelectItem>
                    <SelectItem value="America/Chicago">Central Time</SelectItem>
                    <SelectItem value="America/Denver">Mountain Time</SelectItem>
                    <SelectItem value="America/Los_Angeles">Pacific Time</SelectItem>
                    <SelectItem value="Europe/London">London</SelectItem>
                    <SelectItem value="Europe/Berlin">Berlin</SelectItem>
                    <SelectItem value="Asia/Tokyo">Tokyo</SelectItem>
                  </SelectContent>
                </Select>
              </div>
            </div>
          </div>
        )}
      </CardContent>
    </Card>
  );

  const renderStep5 = () => (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle>Review Configuration</CardTitle>
        </CardHeader>
        <CardContent className="space-y-6">
          <div>
            <h4 className="font-medium mb-2">General Information</h4>
            <div className="bg-gray-50 dark:bg-gray-800 p-4 rounded-lg space-y-2">
              <div><span className="font-medium">Name:</span> {formData.name}</div>
              <div><span className="font-medium">Description:</span> {formData.description || 'No description'}</div>
            </div>
          </div>

          <div>
            <h4 className="font-medium mb-2">Discovery Profiles</h4>
            <div className="bg-gray-50 dark:bg-gray-800 p-4 rounded-lg">
              <div className="flex flex-wrap gap-2">
                {formData.selectedPolicyIds.map(id => {
                  const policy = policies.find((p: ScriptPolicy) => p.id === id);
                  return policy ? (
                    <Badge key={id} variant="secondary">{policy.name}</Badge>
                  ) : null;
                })}
              </div>
            </div>
          </div>

          <div>
            <h4 className="font-medium mb-2">Targets & Configuration</h4>
            <div className="bg-gray-50 dark:bg-gray-800 p-4 rounded-lg space-y-2">
              {Object.entries(formData.targets).map(([key, values]) => {
                const filteredValues = values.filter(v => v.trim() !== '');
                if (filteredValues.length === 0) return null;
                return (
                  <div key={key}>
                    <span className="font-medium capitalize">{key.replace(/([A-Z])/g, ' $1')}:</span>
                    <span className="ml-2">{filteredValues.join(', ')}</span>
                  </div>
                );
              })}
              <div>
                <span className="font-medium">Credential Profile:</span>
                <span className="ml-2">
                  {credentialProfiles.find((p: CredentialProfile) => p.id === formData.credentialProfileId)?.name || 'None'}
                </span>
              </div>
              <div>
                <span className="font-medium">Discovery Probe:</span>
                <span className="ml-2">
                  {probes.find((p: DiscoveryProbe) => p.id === formData.probeId)?.name || 'None'}
                </span>
              </div>
            </div>
          </div>

          <div>
            <h4 className="font-medium mb-2">Schedule</h4>
            <div className="bg-gray-50 dark:bg-gray-800 p-4 rounded-lg">
              {formData.scheduleType === 'now' ? (
                <span>Run immediately after creation</span>
              ) : (
                <div className="space-y-1">
                  <div><span className="font-medium">Type:</span> {formData.schedule.type}</div>
                  {formData.schedule.frequency && (
                    <div><span className="font-medium">Frequency:</span> {formData.schedule.frequency}</div>
                  )}
                  <div><span className="font-medium">Time:</span> {formData.schedule.time} ({formData.schedule.timezone})</div>
                </div>
              )}
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );

  const renderStepIndicator = () => (
    <div className="flex items-center justify-center space-x-4">
      {[1, 2, 3, 4, 5].map((step) => (
        <div key={step} className="flex items-center">
          <div
            className={cn(
              "w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium",
              step < currentStep
                ? "bg-green-500 text-white"
                : step === currentStep
                ? "bg-blue-600 text-white"
                : "bg-gray-200 text-gray-500"
            )}
          >
            {step < currentStep ? <Check className="w-4 h-4" /> : step}
          </div>
          <div className="ml-2 text-sm">
            {step === 1 && "General"}
            {step === 2 && "Profiles"}
            {step === 3 && "Targets"}
            {step === 4 && "Schedule"}
            {step === 5 && "Review"}
          </div>
          {step < 5 && <ChevronRight className="w-4 h-4 text-gray-400 ml-4" />}
        </div>
      ))}
    </div>
  );

  const renderCurrentStep = () => {
    switch (currentStep) {
      case 1: return renderStep1();
      case 2: return renderStep2();
      case 3: return renderStep3();
      case 4: return renderStep4();
      case 5: return renderStep5();
      default: return null;
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
      <div className="bg-white dark:bg-gray-800 rounded-lg shadow-xl w-full max-w-4xl max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200 dark:border-gray-700">
          <h2 className="text-xl font-semibold text-gray-900 dark:text-white">
            Create New Discovery Job
          </h2>
          <Button
            variant="ghost"
            size="sm"
            onClick={() => setLocation('/agentless-discovery')}
            className="text-gray-400 hover:text-gray-600"
          >
            <X className="w-5 h-5" />
          </Button>
        </div>

        {/* Step Indicator */}
        <div className="p-6 border-b border-gray-200 dark:border-gray-700">
          {renderStepIndicator()}
        </div>

        {/* Content */}
        <div className="p-6">
          {renderCurrentStep()}
        </div>

        {/* Footer */}
        <div className="flex items-center justify-between p-6 border-t border-gray-200 dark:border-gray-700">
          <Button
            variant="outline"
            onClick={handleBack}
            disabled={currentStep === 1}
            className="flex items-center space-x-2"
          >
            <ArrowLeft className="w-4 h-4" />
            <span>Back</span>
          </Button>

          <div className="flex items-center space-x-3">
            {currentStep < 5 ? (
              <Button
                onClick={handleNext}
                disabled={!canProceedToNext()}
                className="flex items-center space-x-2 bg-blue-600 hover:bg-blue-700"
              >
                <span>Next</span>
                <ArrowRight className="w-4 h-4" />
              </Button>
            ) : (
              <Button
                onClick={handleSubmit}
                disabled={createJobMutation.isPending}
                className="flex items-center space-x-2 bg-green-600 hover:bg-green-700"
              >
                {createJobMutation.isPending ? (
                  <>
                    <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin" />
                    <span>Creating...</span>
                  </>
                ) : (
                  <>
                    <Play className="w-4 h-4" />
                    <span>Create Job</span>
                  </>
                )}
              </Button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

import { useState } from 'react';
import { useQuery } from '@tanstack/react-query';
import { useRoute, Link } from 'wouter';
import { ChevronLeft, Play, Pause, Square, Edit, Copy, History, AlertTriangle, Trash2, Clock, Target, CheckCircle, XCircle, AlertCircle, Server, Monitor, HardDrive, Wifi, Shield, FileText, Download, RefreshCw } from 'lucide-react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from '@/components/ui/table';
import { Progress } from '@/components/ui/progress';
import { Separator } from '@/components/ui/separator';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { DropdownMenu, DropdownMenuContent, DropdownMenuItem, DropdownMenuSeparator, DropdownMenuTrigger } from '@/components/ui/dropdown-menu';
import { AgentlessDiscoveryJob } from '@/shared/schema';
import { useMutation, useQueryClient } from '@tanstack/react-query';
import { apiRequest } from '@/lib/queryClient';
import { useToast } from '@/hooks/use-toast';

interface DiscoveredAsset {
  hostname: string;
  ip: string;
  os: string;
  type: string;
  vlan?: string;
  compliance?: number;
}

interface ErrorLog {
  timestamp: string;
  level: string;
  message: string;
}

export default function AgentlessJobDetails() {
  const [, params] = useRoute('/agentless-discovery/view/:id');
  const jobId = params?.id ? parseInt(params.id) : null;
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data: job, isLoading } = useQuery<AgentlessDiscoveryJob>({
    queryKey: ['/api/agentless-discovery-jobs', jobId],
    enabled: !!jobId,
  });

  // Action mutations
  const runJobMutation = useMutation({
    mutationFn: async (jobId: number) => {
      return await apiRequest(`/api/agentless-discovery-jobs/${jobId}/run`, {
        method: 'POST',
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Job started successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/agentless-discovery-jobs'] });
    },
    onError: (error: any) => {
      console.error('Failed to start job:', error);
      toast({
        title: "Error",
        description: `Failed to start job: ${error.message || 'Unknown error'}`,
        variant: "destructive",
      });
    },
  });

  const pauseJobMutation = useMutation({
    mutationFn: async (jobId: number) => {
      return await apiRequest(`/api/agentless-discovery-jobs/${jobId}/pause`, {
        method: 'POST',
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Job paused successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/agentless-discovery-jobs'] });
    },
    onError: (error: any) => {
      console.error('Failed to pause job:', error);
      toast({
        title: "Error",
        description: `Failed to pause job: ${error.message || 'Unknown error'}`,
        variant: "destructive",
      });
    },
  });

  const disableJobMutation = useMutation({
    mutationFn: async (jobId: number) => {
      return await apiRequest(`/api/agentless-discovery-jobs/${jobId}/disable`, {
        method: 'POST',
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Job disabled successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/agentless-discovery-jobs'] });
    },
    onError: (error: any) => {
      console.error('Failed to disable job:', error);
      toast({
        title: "Error",
        description: `Failed to disable job: ${error.message || 'Unknown error'}`,
        variant: "destructive",
      });
    },
  });

  const deleteJobMutation = useMutation({
    mutationFn: async (jobId: number) => {
      return await apiRequest(`/api/agentless-discovery-jobs/${jobId}`, {
        method: 'DELETE',
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Job deleted successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/agentless-discovery-jobs'] });
    },
    onError: (error: any) => {
      console.error('Failed to delete job:', error);
      toast({
        title: "Error",
        description: `Failed to delete job: ${error.message || 'Unknown error'}`,
        variant: "destructive",
      });
    },
  });

  const handleJobAction = (action: string) => {
    if (!jobId) return;

    console.log(`Executing action: ${action} on job: ${jobId}`);
    try {
      switch (action) {
        case 'run':
          runJobMutation.mutate(jobId);
          break;
        case 'pause':
          pauseJobMutation.mutate(jobId);
          break;
        case 'disable':
          disableJobMutation.mutate(jobId);
          break;
        case 'delete':
          if (confirm('Are you sure you want to delete this job?')) {
            deleteJobMutation.mutate(jobId);
          }
          break;
        case 'edit':
          toast({
            title: "Info",
            description: "Edit functionality will be implemented soon",
          });
          break;
        case 'clone':
          toast({
            title: "Info", 
            description: "Clone functionality will be implemented soon",
          });
          break;
        case 'history':
          toast({
            title: "Info",
            description: "History view will be implemented soon",
          });
          break;
        default:
          console.warn(`Unknown action: ${action}`);
      }
    } catch (error) {
      console.error('Error in handleJobAction:', error);
      toast({
        title: "Error",
        description: `Failed to execute action: ${action}`,
        variant: "destructive",
      });
    }
  };

  if (!jobId) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-6">
        <div className="max-w-7xl mx-auto">
          <Alert variant="destructive">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>Invalid job ID provided.</AlertDescription>
          </Alert>
        </div>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-6">
        <div className="max-w-7xl mx-auto">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/3" />
            <div className="grid grid-cols-4 gap-4">
              {[...Array(4)].map((_, i) => (
                <div key={i} className="h-24 bg-gray-200 dark:bg-gray-700 rounded" />
              ))}
            </div>
            <div className="h-96 bg-gray-200 dark:bg-gray-700 rounded" />
          </div>
        </div>
      </div>
    );
  }

  if (!job) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-6">
        <div className="max-w-7xl mx-auto">
          <Alert variant="destructive">
            <AlertCircle className="h-4 w-4" />
            <AlertDescription>Job not found.</AlertDescription>
          </Alert>
        </div>
      </div>
    );
  }

  const statusConfig = {
    scheduled: { color: 'bg-blue-500', label: 'Scheduled', icon: Clock, variant: 'default' as const },
    running: { color: 'bg-green-500', label: 'Running', icon: Play, variant: 'default' as const },
    completed: { color: 'bg-emerald-500', label: 'Completed', icon: CheckCircle, variant: 'secondary' as const },
    failed: { color: 'bg-red-500', label: 'Failed', icon: XCircle, variant: 'destructive' as const },
    paused: { color: 'bg-yellow-500', label: 'Paused', icon: Pause, variant: 'secondary' as const },
    disabled: { color: 'bg-gray-500', label: 'Disabled', icon: Square, variant: 'outline' as const },
  };

  const config = statusConfig[job.status as keyof typeof statusConfig];
  const Icon = config?.icon || Clock;

  // Parse discovered assets and error logs
  const discoveredAssets: DiscoveredAsset[] = job.discoveredAssets ? JSON.parse(job.discoveredAssets) : [];
  const errorLogs: ErrorLog[] = job.errorLogs ? JSON.parse(job.errorLogs) : [];
  const targets = job.targets ? JSON.parse(job.targets) : {};
  const schedule = job.schedule ? JSON.parse(job.schedule) : {};
  const policyIds = job.policyIds ? JSON.parse(job.policyIds) : [];

  // Group discovered assets by type
  const groupedAssets = discoveredAssets.reduce((acc, asset) => {
    const type = asset.type || 'Unknown';
    if (!acc[type]) acc[type] = [];
    acc[type].push(asset);
    return acc;
  }, {} as Record<string, DiscoveredAsset[]>);

  const getAssetTypeIcon = (type: string) => {
    const typeIcons: Record<string, any> = {
      'Domain Controller': Server,
      'File Server': HardDrive,
      'Web Server': Monitor,
      'Database Server': HardDrive,
      'Workstation': Monitor,
      'Development Web Server': Monitor,
      'Development Database': HardDrive,
    };
    return typeIcons[type] || Server;
  };

  const successRate = job.runCount > 0 ? (job.successCount / job.runCount) * 100 : 0;

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Link href="/agentless-jobs">
              <Button variant="ghost" size="sm">
                <ChevronLeft className="h-4 w-4 mr-2" />
                Back to Jobs
              </Button>
            </Link>
            <div>
              <h1 className="text-3xl font-bold text-gray-900 dark:text-white">{job.name}</h1>
              <p className="text-gray-600 dark:text-gray-400">{job.description}</p>
            </div>
          </div>

          <div className="flex items-center space-x-3">
            <Badge variant={config?.variant} className="flex items-center space-x-1">
              <div className={`w-2 h-2 rounded-full ${config?.color}`} />
              <span>{config?.label}</span>
            </Badge>

            <DropdownMenu>
              <DropdownMenuTrigger asChild>
                <Button variant="outline">
                  Actions
                </Button>
              </DropdownMenuTrigger>
              <DropdownMenuContent align="end" className="w-48">
                {job.status !== 'running' && job.status !== 'disabled' && (
                  <DropdownMenuItem onClick={() => handleJobAction('run')}>
                    <Play className="mr-2 h-4 w-4" />
                    Run Now
                  </DropdownMenuItem>
                )}
                {job.status === 'running' && (
                  <DropdownMenuItem onClick={() => handleJobAction('pause')}>
                    <Pause className="mr-2 h-4 w-4" />
                    Pause
                  </DropdownMenuItem>
                )}
                <DropdownMenuItem onClick={() => handleJobAction('edit')}>
                  <Edit className="mr-2 h-4 w-4" />
                  Edit
                </DropdownMenuItem>
                <DropdownMenuItem onClick={() => handleJobAction('clone')}>
                  <Copy className="mr-2 h-4 w-4" />
                  Clone
                </DropdownMenuItem>
                <DropdownMenuItem onClick={() => handleJobAction('history')}>
                  <History className="mr-2 h-4 w-4" />
                  View History
                </DropdownMenuItem>
                <DropdownMenuSeparator />
                {job.status !== 'disabled' && (
                  <DropdownMenuItem 
                    onClick={() => handleJobAction('disable')}
                    className="text-yellow-600"
                  >
                    <AlertTriangle className="mr-2 h-4 w-4" />
                    Disable
                  </DropdownMenuItem>
                )}
                <DropdownMenuItem 
                  onClick={() => handleJobAction('delete')}
                  className="text-red-600"
                >
                  <Trash2 className="mr-2 h-4 w-4" />
                  Delete
                </DropdownMenuItem>
              </DropdownMenuContent>
            </DropdownMenu>
          </div>
        </div>

        {/* Summary Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center space-x-2">
                <Target className="h-5 w-5 text-blue-600" />
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Total Runs</p>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">{job.runCount}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center space-x-2">
                <CheckCircle className="h-5 w-5 text-green-600" />
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Success Rate</p>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">{successRate.toFixed(1)}%</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center space-x-2">
                <Server className="h-5 w-5 text-purple-600" />
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Assets Found</p>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">{discoveredAssets.length}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center space-x-2">
                <Clock className="h-5 w-5 text-orange-600" />
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Last Run</p>
                  <p className="text-sm font-bold text-gray-900 dark:text-white">
                    {job.lastRun ? new Date(job.lastRun).toLocaleDateString() : 'Never'}
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Main Content */}
        <Tabs defaultValue="summary" className="space-y-6">
          <TabsList className="grid w-full grid-cols-4">
            <TabsTrigger value="summary">Summary</TabsTrigger>
            <TabsTrigger value="assets">Discovered Assets</TabsTrigger>
            <TabsTrigger value="errors">Error Logs</TabsTrigger>
            <TabsTrigger value="configuration">Configuration</TabsTrigger>
          </TabsList>

          <TabsContent value="summary" className="space-y-6">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <Target className="h-5 w-5" />
                    <span>Execution Statistics</span>
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div className="flex justify-between items-center">
                    <span className="text-sm text-gray-600 dark:text-gray-400">Success Rate</span>
                    <span className="font-medium">{successRate.toFixed(1)}%</span>
                  </div>
                  <Progress value={successRate} className="h-2" />

                  <Separator />

                  <div className="grid grid-cols-3 gap-4 text-center">
                    <div>
                      <p className="text-lg font-bold text-green-600">{job.successCount}</p>
                      <p className="text-xs text-gray-600 dark:text-gray-400">Successful</p>
                    </div>
                    <div>
                      <p className="text-lg font-bold text-red-600">{job.errorCount}</p>
                      <p className="text-xs text-gray-600 dark:text-gray-400">Failed</p>
                    </div>
                    <div>
                      <p className="text-lg font-bold text-gray-600">{job.runCount}</p>
                      <p className="text-xs text-gray-600 dark:text-gray-400">Total</p>
                    </div>
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <Clock className="h-5 w-5" />
                    <span>Schedule Information</span>
                  </CardTitle>
                </CardHeader>
                <CardContent className="space-y-3">
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600 dark:text-gray-400">Type</span>
                    <span className="font-medium">{schedule.type || 'Manual'}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600 dark:text-gray-400">Frequency</span>
                    <span className="font-medium">{schedule.frequency || 'N/A'}</span>
                  </div>
                  {schedule.time && (
                    <div className="flex justify-between">
                      <span className="text-sm text-gray-600 dark:text-gray-400">Time</span>
                      <span className="font-medium">{schedule.time}</span>
                    </div>
                  )}
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600 dark:text-gray-400">Next Run</span>
                    <span className="font-medium">
                      {job.nextRun ? new Date(job.nextRun).toLocaleString() : 'Not scheduled'}
                    </span>
                  </div>
                </CardContent>
              </Card>
            </div>
          </TabsContent>

          <TabsContent value="assets" className="space-y-6">
            {Object.keys(groupedAssets).length === 0 ? (
              <Card>
                <CardContent className="p-12 text-center">
                  <Server className="mx-auto h-12 w-12 text-gray-400" />
                  <h3 className="mt-2 text-sm font-medium text-gray-900 dark:text-white">No assets discovered</h3>
                  <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
                    This job hasn't discovered any assets yet.
                  </p>
                </CardContent>
              </Card>
            ) : (
              <div className="space-y-6">
                {Object.entries(groupedAssets).map(([type, assets]) => {
                  const TypeIcon = getAssetTypeIcon(type);
                  return (
                    <Card key={type}>
                      <CardHeader>
                        <CardTitle className="flex items-center space-x-2">
                          <TypeIcon className="h-5 w-5" />
                          <span>{type}</span>
                          <Badge variant="secondary">{assets.length}</Badge>
                        </CardTitle>
                      </CardHeader>
                      <CardContent>
                        <Table>
                          <TableHeader>
                            <TableRow>
                              <TableHead>Hostname</TableHead>
                              <TableHead>IP Address</TableHead>
                              <TableHead>Operating System</TableHead>
                              {assets.some(a => a.vlan) && <TableHead>VLAN</TableHead>}
                              {assets.some(a => a.compliance) && <TableHead>Compliance</TableHead>}
                            </TableRow>
                          </TableHeader>
                          <TableBody>
                            {assets.map((asset, index) => (
                              <TableRow key={index}>
                                <TableCell className="font-medium">{asset.hostname}</TableCell>
                                <TableCell>{asset.ip}</TableCell>
                                <TableCell>{asset.os}</TableCell>
                                {assets.some(a => a.vlan) && (
                                  <TableCell>{asset.vlan || '-'}</TableCell>
                                )}
                                {assets.some(a => a.compliance) && (
                                  <TableCell>
                                    {asset.compliance ? (
                                      <div className="flex items-center space-x-2">
                                        <Progress value={asset.compliance} className="h-2 w-16" />
                                        <span className="text-sm">{asset.compliance}%</span>
                                      </div>
                                    ) : '-'}
                                  </TableCell>
                                )}
                              </TableRow>
                            ))}
                          </TableBody>
                        </Table>
                      </CardContent>
                    </Card>
                  );
                })}
              </div>
            )}
          </TabsContent>

          <TabsContent value="errors" className="space-y-6">
            {errorLogs.length === 0 ? (
              <Card>
                <CardContent className="p-12 text-center">
                  <CheckCircle className="mx-auto h-12 w-12 text-green-400" />
                  <h3 className="mt-2 text-sm font-medium text-gray-900 dark:text-white">No errors found</h3>
                  <p className="mt-1 text-sm text-gray-500 dark:text-gray-400">
                    This job has run without any errors.
                  </p>
                </CardContent>
              </Card>
            ) : (
              <Card>
                <CardHeader>
                  <CardTitle className="flex items-center space-x-2">
                    <AlertCircle className="h-5 w-5" />
                    <span>Error Logs</span>
                    <Badge variant="destructive">{errorLogs.length}</Badge>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="space-y-3">
                    {errorLogs.map((log, index) => (
                      <Alert key={index} variant={log.level === 'error' ? 'destructive' : 'default'}>
                        <AlertCircle className="h-4 w-4" />
                        <AlertDescription>
                          <div className="flex justify-between items-start">
                            <span>{log.message}</span>
                            <span className="text-xs text-gray-500 ml-4">
                              {new Date(log.timestamp).toLocaleString()}
                            </span>
                          </div>
                        </AlertDescription>
                      </Alert>
                    ))}
                  </div>
                </CardContent>
              </Card>
            )}
          </TabsContent>

          <TabsContent value="configuration" className="space-y-6">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <Card>
                <CardHeader>
                  <CardTitle>Target Configuration</CardTitle>
                </CardHeader>
                <CardContent className="space-y-4">
                  {targets.ipRanges && targets.ipRanges.length > 0 && (
                    <div>
                      <h4 className="font-medium text-sm text-gray-600 dark:text-gray-400">IP Ranges</h4>
                      <div className="mt-1 space-y-1">
                        {targets.ipRanges.map((range: string, index: number) => (
                          <Badge key={index} variant="outline">{range}</Badge>
                        ))}
                      </div>
                    </div>
                  )}

                  {targets.hostnames && targets.hostnames.length > 0 && (
                    <div>
                      <h4 className="font-medium text-sm text-gray-600 dark:text-gray-400">Hostnames</h4>
                      <div className="mt-1 space-y-1">
                        {targets.hostnames.map((hostname: string, index: number) => (
                          <Badge key={index} variant="outline">{hostname}</Badge>
                        ))}
                      </div>
                    </div>
                  )}

                  {targets.ipSegments && targets.ipSegments.length > 0 && (
                    <div>
                      <h4 className="font-medium text-sm text-gray-600 dark:text-gray-400">IP Segments</h4>
                      <div className="mt-1 space-y-1">
                        {targets.ipSegments.map((segment: string, index: number) => (
                          <Badge key={index} variant="outline">{segment}</Badge>
                        ))}
                      </div>
                    </div>
                  )}
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle>Job Configuration</CardTitle>
                </CardHeader>
                <CardContent className="space-y-3">
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600 dark:text-gray-400">Created By</span>
                    <span className="font-medium">{job.createdBy}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600 dark:text-gray-400">Created At</span>
                    <span className="font-medium">{new Date(job.createdAt).toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600 dark:text-gray-400">Last Updated</span>
                    <span className="font-medium">{new Date(job.updatedAt).toLocaleString()}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600 dark:text-gray-400">Policies</span>
                    <span className="font-medium">{policyIds.length} selected</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600 dark:text-gray-400">Credential Profile</span>
                    <span className="font-medium">{job.credentialProfileId || 'Not set'}</span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-sm text-gray-600 dark:text-gray-400">Probe ID</span>
                    <span className="font-medium">{job.probeId || 'Not set'}</span>
                  </div>
                </CardContent>
              </Card>
            </div>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

import { useState } from "react";
import { useQuery, useMutation } from "@tanstack/react-query";
import { useLocation } from "wouter";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Input } from "@/components/ui/input";
import { 
  Select, 
  SelectContent, 
  SelectItem, 
  SelectTrigger, 
  SelectValue 
} from "@/components/ui/select";
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableHead, 
  TableHeader, 
  TableRow 
} from "@/components/ui/table";
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuLabel,
  DropdownMenuSeparator,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu";
import { 
  Plus, 
  Search, 
  MoreHorizontal, 
  Play, 
  Pause, 
  Edit, 
  Copy, 
  History, 
  Trash2,
  Calendar,
  Clock,
  Target,
  Shield,
  CheckCircle,
  XCircle,
  AlertTriangle,
  RefreshCw,
  Filter,
  Server,
  MapPin,
  SortAsc,
  SortDesc,
  Settings
} from "lucide-react";
import { queryClient, apiRequest } from "@/lib/queryClient";
import { useToast } from "@/hooks/use-toast";
import { useLanguage } from "@/contexts/LanguageContext";
import { cn } from "@/lib/utils";
import { format } from "date-fns";
import type { AgentlessDiscoveryJob } from "@shared/schema";

interface JobFilters {
  status: string;
  search: string;
  satelliteServer: string;
  region: string;
  createdBy: string;
  priority: string;
  timeRange: string;
}

const statusConfig = {
  scheduled: { color: "bg-blue-500", label: "Scheduled", icon: Calendar },
  running: { color: "bg-green-500", label: "Running", icon: RefreshCw },
  completed: { color: "bg-gray-500", label: "Completed", icon: CheckCircle },
  paused: { color: "bg-yellow-500", label: "Paused", icon: Pause },
  failed: { color: "bg-red-500", label: "Failed", icon: XCircle },
  disabled: { color: "bg-gray-400", label: "Disabled", icon: AlertTriangle }
};

export default function AgentlessJobsPage() {
  const [, setLocation] = useLocation();
  const [filters, setFilters] = useState<JobFilters>({ 
    status: 'all', 
    search: '', 
    satelliteServer: 'all', 
    region: 'all', 
    createdBy: 'all', 
    priority: 'all', 
    timeRange: 'all' 
  });
  const [sortField, setSortField] = useState<string>('createdAt');
  const [sortOrder, setSortOrder] = useState<'asc' | 'desc'>('desc');
  const [showAdvancedFilters, setShowAdvancedFilters] = useState(false);
  const { toast } = useToast();
  const { t } = useLanguage();

  // Fetch agentless discovery jobs
  const { data: jobs = [], isLoading, refetch } = useQuery({
    queryKey: ['/api/agentless-discovery-jobs'],
  });

  // Job action mutations
  const runJobMutation = useMutation({
    mutationFn: async (jobId: number) => {
      return await apiRequest(`/api/agentless-discovery-jobs/${jobId}/run`, {
        method: 'POST',
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Job started successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/agentless-discovery-jobs'] });
    },
    onError: (error: any) => {
      console.error('Failed to start job:', error);
      toast({
        title: "Error",
        description: `Failed to start job: ${error.message || 'Unknown error'}`,
        variant: "destructive",
      });
    },
  });

  const pauseJobMutation = useMutation({
    mutationFn: async (jobId: number) => {
      return await apiRequest(`/api/agentless-discovery-jobs/${jobId}/pause`, {
        method: 'POST',
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Job paused successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/agentless-discovery-jobs'] });
    },
    onError: (error: any) => {
      console.error('Failed to pause job:', error);
      toast({
        title: "Error",
        description: `Failed to pause job: ${error.message || 'Unknown error'}`,
        variant: "destructive",
      });
    },
  });

  const disableJobMutation = useMutation({
    mutationFn: async (jobId: number) => {
      return await apiRequest(`/api/agentless-discovery-jobs/${jobId}/disable`, {
        method: 'POST',
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Job disabled successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/agentless-discovery-jobs'] });
    },
    onError: (error: any) => {
      console.error('Failed to disable job:', error);
      toast({
        title: "Error",
        description: `Failed to disable job: ${error.message || 'Unknown error'}`,
        variant: "destructive",
      });
    },
  });

  const deleteJobMutation = useMutation({
    mutationFn: async (jobId: number) => {
      return await apiRequest(`/api/agentless-discovery-jobs/${jobId}`, {
        method: 'DELETE',
      });
    },
    onSuccess: () => {
      toast({
        title: "Success",
        description: "Job deleted successfully",
      });
      queryClient.invalidateQueries({ queryKey: ['/api/agentless-discovery-jobs'] });
    },
    onError: (error: any) => {
      console.error('Failed to delete job:', error);
      toast({
        title: "Error",
        description: `Failed to delete job: ${error.message || 'Unknown error'}`,
        variant: "destructive",
      });
    },
  });

  // Mock satellite servers and regions for filtering
  const mockSatelliteServers = [
    { id: 1, name: "Enterprise-Server-01", region: "North America", location: "New York DC" },
    { id: 2, name: "Enterprise-Server-02", region: "North America", location: "Los Angeles DC" },
    { id: 3, name: "Enterprise-Server-03", region: "Europe", location: "London DC" },
    { id: 4, name: "Enterprise-Server-04", region: "Asia-Pacific", location: "Tokyo DC" },
    { id: 5, name: "Enterprise-Server-05", region: "Europe", location: "Frankfurt DC" }
  ];

  const mockRegions = ["North America", "Europe", "Asia-Pacific", "South America", "Africa"];
  const mockCreators = ["admin", "operator", "john.doe", "jane.smith", "system"];
  const mockPriorities = ["High", "Medium", "Low", "Critical"];

  // Enhanced filtering logic
  const filteredJobs = jobs.filter((job: AgentlessDiscoveryJob) => {
    const matchesSearch = !filters.search || 
      job.name.toLowerCase().includes(filters.search.toLowerCase()) ||
      job.description?.toLowerCase().includes(filters.search.toLowerCase());

    const matchesStatus = filters.status === 'all' || !filters.status || job.status === filters.status;

    const matchesSatelliteServer = filters.satelliteServer === 'all' || !filters.satelliteServer || 
      job.probeId?.toString() === filters.satelliteServer;

    const matchesRegion = filters.region === 'all' || !filters.region || 
      mockSatelliteServers.find(s => s.id === job.probeId)?.region === filters.region;

    const matchesCreatedBy = filters.createdBy === 'all' || !filters.createdBy || 
      job.createdBy?.toString() === filters.createdBy;

    const matchesPriority = filters.priority === 'all' || !filters.priority || 
      (job as any).priority === filters.priority;

    const matchesTimeRange = filters.timeRange === 'all' || !filters.timeRange || checkTimeRange(job, filters.timeRange);

    return matchesSearch && matchesStatus && matchesSatelliteServer && 
           matchesRegion && matchesCreatedBy && matchesPriority && matchesTimeRange;
  });

  // Sort filtered jobs
  const sortedJobs = [...filteredJobs].sort((a, b) => {
    const multiplier = sortOrder === 'asc' ? 1 : -1;

    switch (sortField) {
      case 'name':
        return multiplier * a.name.localeCompare(b.name);
      case 'status':
        return multiplier * a.status.localeCompare(b.status);
      case 'createdAt':
        return multiplier * (new Date(a.createdAt || 0).getTime() - new Date(b.createdAt || 0).getTime());
      case 'lastRun':
        return multiplier * (new Date(a.startedAt || 0).getTime() - new Date(b.startedAt || 0).getTime());
      default:
        return 0;
    }
  });

  const checkTimeRange = (job: AgentlessDiscoveryJob, timeRange: string) => {
    if (!job.createdAt) return true;

    const now = new Date();
    const jobDate = new Date(job.createdAt);
    const diffHours = (now.getTime() - jobDate.getTime()) / (1000 * 60 * 60);

    switch (timeRange) {
      case 'today':
        return diffHours <= 24;
      case 'week':
        return diffHours <= 168;
      case 'month':
        return diffHours <= 720;
      case 'quarter':
        return diffHours <= 2160;
      default:
        return true;
    }
  };

  const handleSort = (field: string) => {
    if (sortField === field) {
      setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc');
    } else {
      setSortField(field);
      setSortOrder('asc');
    }
  };

  const getSortIcon = (field: string) => {
    if (sortField !== field) return null;
    return sortOrder === 'asc' ? <SortAsc className="w-4 h-4" /> : <SortDesc className="w-4 h-4" />;
  };

  const clearAllFilters = () => {
    setFilters({ 
      status: 'all', 
      search: '', 
      satelliteServer: 'all', 
      region: 'all', 
      createdBy: 'all', 
      priority: 'all', 
      timeRange: 'all' 
    });
  };

  const getJobStats = () => {
    const total = jobs.length;
    const running = jobs.filter((job: AgentlessDiscoveryJob) => job.status === 'running').length;
    const scheduled = jobs.filter((job: AgentlessDiscoveryJob) => job.status === 'scheduled').length;
    const failed = jobs.filter((job: AgentlessDiscoveryJob) => job.status === 'failed').length;

    return { total, running, scheduled, failed };
  };

  const handleJobAction = (action: string, jobId: number) => {
    console.log(`Executing action: ${action} on job: ${jobId}`);
    try {
      switch (action) {
        case 'run':
          runJobMutation.mutate(jobId);
          break;
        case 'pause':
          pauseJobMutation.mutate(jobId);
          break;
        case 'disable':
          disableJobMutation.mutate(jobId);
          break;
        case 'delete':
          if (confirm('Are you sure you want to delete this job?')) {
            deleteJobMutation.mutate(jobId);
          }
          break;
        case 'edit':
          toast({
            title: "Info",
            description: "Edit functionality will be implemented soon",
          });
          break;
        case 'clone':
          toast({
            title: "Info", 
            description: "Clone functionality will be implemented soon",
          });
          break;
        case 'history':
          toast({
            title: "Info",
            description: "History view will be implemented soon",
          });
          break;
        case 'view':
          setLocation(`/agentless-discovery/view/${jobId}`);
          break;
        default:
          console.warn(`Unknown action: ${action}`);
      }
    } catch (error) {
      console.error('Error in handleJobAction:', error);
      toast({
        title: "Error",
        description: `Failed to execute action: ${action}`,
        variant: "destructive",
      });
    }
  };

  const stats = getJobStats();

  const StatusBadge = ({ status }: { status: string }) => {
    const config = statusConfig[status as keyof typeof statusConfig];
    if (!config) return <Badge variant="outline">{status}</Badge>;

    const Icon = config.icon;
    return (
      <Badge variant="outline" className="flex items-center space-x-1">
        <div className={`w-2 h-2 rounded-full ${config.color}`} />
        <span>{config.label}</span>
      </Badge>
    );
  };

  if (isLoading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-6">
        <div className="max-w-7xl mx-auto">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-gray-200 dark:bg-gray-700 rounded w-1/3" />
            <div className="grid grid-cols-4 gap-4">
              {[...Array(4)].map((_, i) => (
                <div key={i} className="h-24 bg-gray-200 dark:bg-gray-700 rounded" />
              ))}
            </div>
            <div className="h-96 bg-gray-200 dark:bg-gray-700 rounded" />
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 p-6">
      <div className="max-w-7xl mx-auto space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold text-gray-900 dark:text-white">
              Agentless Discovery Jobs
            </h1>
            <p className="text-gray-600 dark:text-gray-400 mt-2">
              Manage and monitor your automated network discovery and compliance scanning jobs
            </p>
          </div>
          <Button 
            onClick={() => setLocation('/agentless-discovery')}
            className="bg-blue-600 hover:bg-blue-700"
          >
            <Plus className="w-4 h-4 mr-2" />
            Create New Job
          </Button>
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Total Jobs</p>
                  <p className="text-2xl font-bold text-gray-900 dark:text-white">{stats.total}</p>
                </div>
                <Target className="w-8 h-8 text-blue-500" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Running</p>
                  <p className="text-2xl font-bold text-green-600">{stats.running}</p>
                </div>
                <RefreshCw className="w-8 h-8 text-green-500" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-gray-600 dark:text-gray-400">Scheduled</p>
