                      </TableCell>
                      <TableCell>
                        <Badge variant="outline">{targetCount} targets</Badge>
                      </TableCell>
                      <TableCell>
                        <div className="text-sm">
                          <div className="font-medium">{job.createdBy}</div>
                          {job.createdAt && (
                            <div className="text-xs text-gray-500">
                              {format(new Date(job.createdAt), 'MMM dd, yyyy')}
                            </div>
