                              <FormItem>
                                <FormLabel>Specific Hostnames</FormLabel>
                                <FormControl>
                                  <Textarea
                                    placeholder="server01.domain.com&#10;workstation-123&#10;db-server"
                                    value={field.value?.join('\n') || ''}
