                              <div className="space-y-1 leading-none">
                                <FormLabel>Auto Update</FormLabel>
                                <FormDescription>
                                  Enable automatic agent updates
                                </FormDescription>
                              </div>
                            </FormItem>
                          )}
                        />
                      </div>
