    public int RetryCount { get; set; } = 0;

    public bool ReportedToServer { get; set; } = false;

    public DateTime? ReportedAt { get; set; }

    public DateTime CreatedAt { get; set; }

    public DateTime? UpdatedAt { get; set; }
