using Microsoft.EntityFrameworkCore;
using Microsoft.Extensions.Logging;
using System.Text.Json;
using UEM.Endpoint.Agent.Data.Contexts;
using UEM.Endpoint.Agent.Data.Models;
using UEM.Endpoint.Agent.Services;

namespace UEM.Endpoint.Agent.Data.Services;

/// <summary>
/// Data service for policy execution management in local SQLite database
/// </summary>
public class PolicyDataService
{
    private readonly AgentDataContext _context;
    private readonly ILogger<PolicyDataService> _logger;

    public PolicyDataService(AgentDataContext context, ILogger<PolicyDataService> logger)
    {
        _context = context;
        _logger = logger;
    }

    #region Policy Command Operations

    /// <summary>
    /// Store a pending policy command
    /// </summary>
    public async Task<int> StorePendingCommandAsync(PolicyExecutionCommand command, CancellationToken cancellationToken = default)
    {
        using var timer = _logger.BeginPerformanceTimer("StorePendingCommand");

        try
        {
            var commandRecord = new PolicyCommandRecord
            {
                ExecutionId = command.ExecutionId,
                AgentId = command.AgentId,
                PolicyId = command.PolicyId,
                PolicyName = command.PolicyName,
                ExecutionStepsJson = JsonSerializer.Serialize(command.ExecutionSteps),
                TimeoutSeconds = command.TimeoutSeconds,
                TriggerType = command.TriggerType,
                TriggeredBy = command.TriggeredBy,
                IssuedAt = command.IssuedAt,
                ExpiresAt = command.ExpiresAt,
                MetadataJson = command.Metadata != null ? JsonSerializer.Serialize(command.Metadata) : null,
                Status = "pending",
                ReceivedAt = DateTime.UtcNow
            };

            _context.PolicyCommands.Add(commandRecord);
            await _context.SaveChangesAsync(cancellationToken);

            _logger.LogInformation("Stored pending policy command {ExecutionId} for policy {PolicyId}", 
                command.ExecutionId, command.PolicyId);

            return commandRecord.Id;
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to store pending command {ExecutionId}", command.ExecutionId);
            throw;
        }
    }

    /// <summary>
    /// Get pending policy commands for execution
    /// </summary>
    public async Task<List<PolicyExecutionCommand>> GetPendingCommandsAsync(CancellationToken cancellationToken = default)
    {
        try
        {
            var pendingRecords = await _context.PolicyCommands
                .Where(c => c.Status == "pending" && c.ExpiresAt > DateTime.UtcNow)
                .OrderBy(c => c.IssuedAt)
                .ToListAsync(cancellationToken);

            var commands = new List<PolicyExecutionCommand>();

            foreach (var record in pendingRecords)
            {
                try
                {
                    var executionSteps = JsonSerializer.Deserialize<List<PolicyExecutionStep>>(record.ExecutionStepsJson) ?? new List<PolicyExecutionStep>();
                    var metadata = record.MetadataJson != null ? JsonSerializer.Deserialize<Dictionary<string, object>>(record.MetadataJson) : null;

                    commands.Add(new PolicyExecutionCommand
                    {
                        ExecutionId = record.ExecutionId,
                        AgentId = record.AgentId,
                        PolicyId = record.PolicyId,
                        PolicyName = record.PolicyName,
                        ExecutionSteps = executionSteps,
                        TimeoutSeconds = record.TimeoutSeconds,
                        TriggerType = record.TriggerType,
                        TriggeredBy = record.TriggeredBy,
                        IssuedAt = record.IssuedAt,
                        ExpiresAt = record.ExpiresAt,
                        Metadata = metadata
                    });
                }
                catch (JsonException ex)
                {
                    _logger.LogError(ex, "Failed to deserialize command {ExecutionId}, marking as failed", record.ExecutionId);
                    record.Status = "failed";
                    record.ErrorMessage = "Failed to deserialize command data";
                }
            }

            await _context.SaveChangesAsync(cancellationToken);
            return commands;
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to get pending commands");
            return new List<PolicyExecutionCommand>();
        }
    }

    /// <summary>
    /// Mark command as completed
    /// </summary>
    public async Task MarkCommandCompletedAsync(string executionId, CancellationToken cancellationToken = default)
    {
        try
        {
            var command = await _context.PolicyCommands
                .FirstOrDefaultAsync(c => c.ExecutionId == executionId, cancellationToken);

            if (command != null)
            {
                command.Status = "completed";
                command.CompletedAt = DateTime.UtcNow;
                await _context.SaveChangesAsync(cancellationToken);

                _logger.LogInformation("Marked command {ExecutionId} as completed", executionId);
            }
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to mark command {ExecutionId} as completed", executionId);
        }
    }

    /// <summary>
    /// Mark command as failed
    /// </summary>
    public async Task MarkCommandFailedAsync(string executionId, string errorMessage, CancellationToken cancellationToken = default)
    {
        try
        {
            var command = await _context.PolicyCommands
                .FirstOrDefaultAsync(c => c.ExecutionId == executionId, cancellationToken);

            if (command != null)
            {
                command.Status = "failed";
                command.ErrorMessage = errorMessage;
                command.CompletedAt = DateTime.UtcNow;
                await _context.SaveChangesAsync(cancellationToken);

                _logger.LogInformation("Marked command {ExecutionId} as failed: {ErrorMessage}", executionId, errorMessage);
            }
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to mark command {ExecutionId} as failed", executionId);
        }
    }

    #endregion

    #region Policy Execution Results Operations

    /// <summary>
    /// Store policy execution result
    /// </summary>
    public async Task<int> StoreExecutionResultAsync(PolicyExecutionResult result, CancellationToken cancellationToken = default)
    {
        using var timer = _logger.BeginPerformanceTimer("StoreExecutionResult");

        try
        {
            var resultRecord = new PolicyExecutionResultRecord
            {
                ExecutionId = result.ExecutionId,
                AgentId = result.AgentId,
                PolicyId = result.PolicyId,
                Status = result.Status,
                Progress = result.Progress,
                TotalSteps = result.TotalSteps,
                CompletedSteps = result.CompletedSteps,
                CurrentStep = result.CurrentStep,
                StepResultsJson = JsonSerializer.Serialize(result.StepResults),
                FinalOutput = result.FinalOutput,
                FinalStatus = result.FinalStatus,
                ErrorSummary = result.ErrorSummary,
                TotalExecutionTimeMs = result.TotalExecutionTimeMs,
                StartedAt = result.StartedAt,
                CompletedAt = result.CompletedAt,
                AgentVersion = result.AgentVersion,
                OperatingSystem = result.OperatingSystem,
                OsVersion = result.OsVersion,
                RetryCount = result.RetryCount,
                ReportedToServer = false,
                CreatedAt = DateTime.UtcNow
            };

            _context.PolicyExecutionResults.Add(resultRecord);
            await _context.SaveChangesAsync(cancellationToken);

            _logger.LogInformation("Stored execution result for {ExecutionId} with status {Status}", 
                result.ExecutionId, result.Status);

            return resultRecord.Id;
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to store execution result for {ExecutionId}", result.ExecutionId);
            throw;
        }
    }

    /// <summary>
    /// Update existing execution result
    /// </summary>
    public async Task UpdateExecutionResultAsync(PolicyExecutionResult result, CancellationToken cancellationToken = default)
    {
        try
        {
            var existingRecord = await _context.PolicyExecutionResults
                .FirstOrDefaultAsync(r => r.ExecutionId == result.ExecutionId, cancellationToken);

            if (existingRecord != null)
            {
                existingRecord.Status = result.Status;
                existingRecord.Progress = result.Progress;
                existingRecord.CompletedSteps = result.CompletedSteps;
                existingRecord.CurrentStep = result.CurrentStep;
                existingRecord.StepResultsJson = JsonSerializer.Serialize(result.StepResults);
                existingRecord.FinalOutput = result.FinalOutput;
                existingRecord.FinalStatus = result.FinalStatus;
                existingRecord.ErrorSummary = result.ErrorSummary;
                existingRecord.TotalExecutionTimeMs = result.TotalExecutionTimeMs;
                existingRecord.CompletedAt = result.CompletedAt;
                existingRecord.RetryCount = result.RetryCount;
                existingRecord.UpdatedAt = DateTime.UtcNow;

                await _context.SaveChangesAsync(cancellationToken);

                _logger.LogDebug("Updated execution result for {ExecutionId}", result.ExecutionId);
            }
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to update execution result for {ExecutionId}", result.ExecutionId);
        }
    }

    /// <summary>
    /// Get unreported execution results for sending to server
    /// </summary>
    public async Task<List<PolicyExecutionResult>> GetUnreportedExecutionResultsAsync(CancellationToken cancellationToken = default)
    {
        try
        {
            var unreportedRecords = await _context.PolicyExecutionResults
                .Where(r => !r.ReportedToServer && r.Status == "completed")
                .OrderBy(r => r.CreatedAt)
                .Take(50) // Limit batch size
                .ToListAsync(cancellationToken);

            var results = new List<PolicyExecutionResult>();

            foreach (var record in unreportedRecords)
            {
                try
                {
                    var stepResults = JsonSerializer.Deserialize<List<PolicyStepResult>>(record.StepResultsJson) ?? new List<PolicyStepResult>();

                    results.Add(new PolicyExecutionResult
                    {
                        ExecutionId = record.ExecutionId,
                        AgentId = record.AgentId,
                        PolicyId = record.PolicyId,
                        Status = record.Status,
                        Progress = record.Progress,
                        TotalSteps = record.TotalSteps,
                        CompletedSteps = record.CompletedSteps,
                        CurrentStep = record.CurrentStep,
                        StepResults = stepResults,
                        FinalOutput = record.FinalOutput,
                        FinalStatus = record.FinalStatus,
                        ErrorSummary = record.ErrorSummary,
                        TotalExecutionTimeMs = record.TotalExecutionTimeMs,
                        StartedAt = record.StartedAt,
                        CompletedAt = record.CompletedAt,
                        AgentVersion = record.AgentVersion,
                        OperatingSystem = record.OperatingSystem,
                        OsVersion = record.OsVersion,
                        RetryCount = record.RetryCount,
                        ReportedAt = DateTime.UtcNow
                    });
                }
                catch (JsonException ex)
                {
                    _logger.LogError(ex, "Failed to deserialize execution result {ExecutionId}", record.ExecutionId);
                }
            }

            return results;
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to get unreported execution results");
            return new List<PolicyExecutionResult>();
        }
    }

    /// <summary>
    /// Mark execution result as reported to server
    /// </summary>
    public async Task MarkExecutionResultReportedAsync(string executionId, CancellationToken cancellationToken = default)
    {
        try
        {
            var result = await _context.PolicyExecutionResults
                .FirstOrDefaultAsync(r => r.ExecutionId == executionId, cancellationToken);

            if (result != null)
            {
                result.ReportedToServer = true;
                result.ReportedAt = DateTime.UtcNow;
                await _context.SaveChangesAsync(cancellationToken);

                _logger.LogDebug("Marked execution result {ExecutionId} as reported", executionId);
            }
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to mark execution result {ExecutionId} as reported", executionId);
        }
    }

    /// <summary>
    /// Get execution result by execution ID
    /// </summary>
    public async Task<PolicyExecutionResult?> GetExecutionResultAsync(string executionId, CancellationToken cancellationToken = default)
    {
        try
        {
            var record = await _context.PolicyExecutionResults
                .FirstOrDefaultAsync(r => r.ExecutionId == executionId, cancellationToken);

            if (record == null)
                return null;

            var stepResults = JsonSerializer.Deserialize<List<PolicyStepResult>>(record.StepResultsJson) ?? new List<PolicyStepResult>();

            return new PolicyExecutionResult
            {
                ExecutionId = record.ExecutionId,
                AgentId = record.AgentId,
                PolicyId = record.PolicyId,
                Status = record.Status,
                Progress = record.Progress,
                TotalSteps = record.TotalSteps,
                CompletedSteps = record.CompletedSteps,
                CurrentStep = record.CurrentStep,
                StepResults = stepResults,
                FinalOutput = record.FinalOutput,
                FinalStatus = record.FinalStatus,
                ErrorSummary = record.ErrorSummary,
                TotalExecutionTimeMs = record.TotalExecutionTimeMs,
                StartedAt = record.StartedAt,
                CompletedAt = record.CompletedAt,
                AgentVersion = record.AgentVersion,
                OperatingSystem = record.OperatingSystem,
                OsVersion = record.OsVersion,
                RetryCount = record.RetryCount
            };
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to get execution result for {ExecutionId}", executionId);
            return null;
        }
    }

    #endregion

    #region Policy Statistics and Management

    /// <summary>
    /// Get policy execution statistics
    /// </summary>
    public async Task<Dictionary<string, object>> GetPolicyStatisticsAsync(CancellationToken cancellationToken = default)
    {
        try
        {
            var totalCommands = await _context.PolicyCommands.CountAsync(cancellationToken);
            var pendingCommands = await _context.PolicyCommands.CountAsync(c => c.Status == "pending", cancellationToken);
            var completedCommands = await _context.PolicyCommands.CountAsync(c => c.Status == "completed", cancellationToken);
            var failedCommands = await _context.PolicyCommands.CountAsync(c => c.Status == "failed", cancellationToken);

            var totalResults = await _context.PolicyExecutionResults.CountAsync(cancellationToken);
            var unreportedResults = await _context.PolicyExecutionResults.CountAsync(r => !r.ReportedToServer, cancellationToken);
            var successfulResults = await _context.PolicyExecutionResults.CountAsync(r => r.FinalStatus == "success", cancellationToken);
            var failedResults = await _context.PolicyExecutionResults.CountAsync(r => r.FinalStatus == "failed", cancellationToken);

            return new Dictionary<string, object>
            {
                ["Commands"] = new
                {
                    Total = totalCommands,
                    Pending = pendingCommands,
                    Completed = completedCommands,
                    Failed = failedCommands
                },
                ["ExecutionResults"] = new
                {
                    Total = totalResults,
                    Unreported = unreportedResults,
                    Successful = successfulResults,
                    Failed = failedResults,
                    SuccessRate = totalResults > 0 ? (double)successfulResults / totalResults * 100 : 0
                },
                ["GeneratedAt"] = DateTime.UtcNow
            };
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to get policy statistics");
            return new Dictionary<string, object>
            {
                ["Error"] = ex.Message,
                ["GeneratedAt"] = DateTime.UtcNow
            };
        }
    }

    /// <summary>
    /// Clean up old policy data
    /// </summary>
    public async Task CleanupOldDataAsync(int retentionDays = 30, CancellationToken cancellationToken = default)
    {
        try
        {
            var cutoffDate = DateTime.UtcNow.AddDays(-retentionDays);

            // Clean up old completed commands
            var oldCommands = await _context.PolicyCommands
                .Where(c => (c.Status == "completed" || c.Status == "failed") && c.CompletedAt < cutoffDate)
                .ToListAsync(cancellationToken);

            if (oldCommands.Count > 0)
            {
                _context.PolicyCommands.RemoveRange(oldCommands);
                _logger.LogInformation("Removing {Count} old policy commands", oldCommands.Count);
            }

            // Clean up old reported execution results
            var oldResults = await _context.PolicyExecutionResults
                .Where(r => r.ReportedToServer && r.CreatedAt < cutoffDate)
                .ToListAsync(cancellationToken);

            if (oldResults.Count > 0)
            {
                _context.PolicyExecutionResults.RemoveRange(oldResults);
                _logger.LogInformation("Removing {Count} old execution results", oldResults.Count);
            }

            await _context.SaveChangesAsync(cancellationToken);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Failed to cleanup old policy data");
        }
    }

    #endregion
}

UEM.Endpoint.Agent/Services/PolicyExecut.content:
using Microsoft.Extensions.Configuration;
using Microsoft.Extensions.Hosting;
using Microsoft.Extensions.Logging;
using System.Diagnostics;
using System.Text.Json;
using System.Text.Json.Serialization;
using UEM.Endpoint.Agent.Data.Services;

namespace UEM.Endpoint.Agent.Services;

/// <summary>
/// Enterprise-grade policy execution engine for dynamic script execution
/// </summary>
public class PolicyExecutionEngine : BackgroundService
{
    private readonly ILogger<PolicyExecutionEngine> _logger;
    private readonly IConfiguration _configuration;
    private readonly AgentRegistrationService _registrationService;
    private readonly PolicyDataService _policyDataService;
    private readonly HttpClient _httpClient;
    private readonly TimeSpan _pollInterval;
    private readonly ScriptExecutionService _scriptExecutor;

    public PolicyExecutionEngine(
        ILogger<PolicyExecutionEngine> logger,
        IConfiguration configuration,
        AgentRegistrationService registrationService,
        PolicyDataService policyDataService,
        ScriptExecutionService scriptExecutor)
    {
        _logger = logger;
        _configuration = configuration;
        _registrationService = registrationService;
        _policyDataService = policyDataService;
        _scriptExecutor = scriptExecutor;

        // Configure HTTP client
        var handler = new SocketsHttpHandler
        {
            PooledConnectionIdleTimeout = TimeSpan.FromMinutes(2),
            KeepAlivePingPolicy = HttpKeepAlivePingPolicy.Always,
            KeepAlivePingDelay = TimeSpan.FromSeconds(15),
            KeepAlivePingTimeout = TimeSpan.FromSeconds(5),
            SslOptions = new System.Net.Security.SslClientAuthenticationOptions
            {
                RemoteCertificateValidationCallback = (_, __, ___, ____) => true
            }
        };

        _httpClient = new HttpClient(handler) 
        { 
            Timeout = TimeSpan.FromMinutes(10) // Extended timeout for policy operations
        };

        _pollInterval = TimeSpan.FromSeconds(_configuration.GetValue<int>("Policy:PollIntervalSeconds", 30));

        _logger.LogInformation("Policy Execution Engine initialized. Poll Interval: {Interval}", _pollInterval);
    }

    protected override async Task ExecuteAsync(CancellationToken stoppingToken)
    {
        while (!stoppingToken.IsCancellationRequested)
        {
            try
            {
                await PollForPendingPoliciesAsync(stoppingToken);
                await ProcessPendingExecutionsAsync(stoppingToken);
                await ReportExecutionResultsAsync(stoppingToken);

                await Task.Delay(_pollInterval, stoppingToken);
            }
            catch (OperationCanceledException) when (stoppingToken.IsCancellationRequested)
            {
                _logger.LogInformation("Policy execution engine stopping due to cancellation");
                break;
            }
            catch (Exception ex)
            {
                _logger.LogError(ex, "Error in policy execution engine main loop");
                await Task.Delay(TimeSpan.FromMinutes(1), stoppingToken); // Backoff on error
            }
        }
    }

    /// <summary>
    /// Poll for pending policy commands from Satellite API
    /// </summary>
    private async Task PollForPendingPoliciesAsync(CancellationToken cancellationToken)
    {
        if (string.IsNullOrEmpty(_registrationService.AgentId))
        {
            _logger.LogDebug("Agent not registered, skipping policy poll");
            return;
        }

        try
        {
            var satelliteUrl = Environment.GetEnvironmentVariable("SATELLITE_BASE_URL") ?? "https://localhost:7200";
            var requestUri = $"{satelliteUrl}/api/policy/agent/{_registrationService.AgentId}/pending-commands";

            var request = new HttpRequestMessage(HttpMethod.Get, requestUri);
            if (!string.IsNullOrEmpty(_registrationService.Jwt))
            {
                request.Headers.Authorization = new System.Net.Http.Headers.AuthenticationHeaderValue("Bearer", _registrationService.Jwt);
            }

            using var response = await _httpClient.SendAsync(request, cancellationToken);
            if (response.IsSuccessStatusCode)
            {
                var commandsJson = await response.Content.ReadAsStringAsync(cancellationToken);
                var commands = JsonSerializer.Deserialize<List<PolicyExecutionCommand>>(commandsJson);

                if (commands?.Count > 0)
                {
                    _logger.LogInformation("Retrieved {CommandCount} pending policy commands", commands.Count);

                    foreach (var command in commands)
                    {
                        await _policyDataService.StorePendingCommandAsync(command, cancellationToken);

                        // Acknowledge command receipt
                        await AcknowledgeCommandAsync(command.ExecutionId, cancellationToken);
                    }
                }
            }
            else if (response.StatusCode != System.Net.HttpStatusCode.NotFound)
            {
                _logger.LogWarning("Failed to retrieve pending policies: {StatusCode}", response.StatusCode);
            }
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error polling for pending policies");
        }
    }

    /// <summary>
    /// Process pending policy executions
    /// </summary>
    private async Task ProcessPendingExecutionsAsync(CancellationToken cancellationToken)
    {
        try
        {
            var pendingCommands = await _policyDataService.GetPendingCommandsAsync(cancellationToken);

            foreach (var command in pendingCommands)
            {
                if (cancellationToken.IsCancellationRequested)
                    break;

                try
                {
                    await ExecutePolicyAsync(command, cancellationToken);
                }
                catch (Exception ex)
                {
                    _logger.LogError(ex, "Error executing policy {ExecutionId}", command.ExecutionId);
                    await RecordExecutionFailureAsync(command, ex.Message, cancellationToken);
                }
            }
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error processing pending executions");
        }
    }

    /// <summary>
    /// Execute a single policy with all its steps
    /// </summary>
    private async Task ExecutePolicyAsync(PolicyExecutionCommand command, CancellationToken cancellationToken)
    {
        _logger.LogInformation("Starting execution of policy {PolicyId} ({PolicyName}) - Execution ID: {ExecutionId}", 
            command.PolicyId, command.PolicyName, command.ExecutionId);

        var executionResult = new PolicyExecutionResult
        {
            ExecutionId = command.ExecutionId,
            AgentId = command.AgentId,
            PolicyId = command.PolicyId,
            Status = "running",
            TotalSteps = command.ExecutionSteps.Count,
            CompletedSteps = 0,
            CurrentStep = 1,
            StartedAt = DateTime.UtcNow,
            AgentVersion = GetAgentVersion(),
            OperatingSystem = Environment.OSVersion.Platform.ToString(),
            OsVersion = Environment.OSVersion.VersionString
        };

        var stepResults = new List<PolicyStepResult>();
        var overallSuccess = true;
        var stopExecution = false;

        try
        {
            await _policyDataService.StoreExecutionResultAsync(executionResult, cancellationToken);

            foreach (var step in command.ExecutionSteps.OrderBy(s => s.StepNumber))
            {
                if (cancellationToken.IsCancellationRequested || stopExecution)
                    break;

                executionResult.CurrentStep = step.StepNumber;
                executionResult.Progress = (float)executionResult.CompletedSteps / executionResult.TotalSteps * 100;

                var stepResult = await ExecuteStepAsync(step, cancellationToken);
                stepResults.Add(stepResult);

                if (stepResult.Status == "success")
                {
                    executionResult.CompletedSteps++;

                    if (step.OnSuccess == "stop")
                    {
                        _logger.LogInformation("Step {StepNumber} succeeded with stop condition", step.StepNumber);
                        stopExecution = true;
                    }
                }
                else
                {
                    overallSuccess = false;

                    if (step.OnFailure == "stop")
                    {
                        _logger.LogWarning("Step {StepNumber} failed with stop condition", step.StepNumber);
                        stopExecution = true;
                    }
                    else if (step.OnFailure == "retry" && step.MaxRetries > 0)
                    {
                        // Implement retry logic
                        for (int retry = 1; retry <= step.MaxRetries; retry++)
                        {
                            _logger.LogInformation("Retrying step {StepNumber}, attempt {Attempt}/{MaxRetries}", 
                                step.StepNumber, retry, step.MaxRetries);

                            await Task.Delay(TimeSpan.FromSeconds(Math.Min(retry * 2, 30)), cancellationToken);

                            var retryResult = await ExecuteStepAsync(step, cancellationToken);
                            if (retryResult.Status == "success")
                            {
                                stepResult = retryResult;
                                executionResult.CompletedSteps++;
                                overallSuccess = true;
                                break;
                            }
                        }
                    }
                }

                // Update execution result
                executionResult.StepResults = stepResults;
                await _policyDataService.UpdateExecutionResultAsync(executionResult, cancellationToken);
            }

            // Finalize execution
            executionResult.Status = "completed";
            executionResult.Progress = 100.0f;
            executionResult.CompletedAt = DateTime.UtcNow;
            executionResult.TotalExecutionTimeMs = (long)(executionResult.CompletedAt.Value - executionResult.StartedAt.Value).TotalMilliseconds;

            if (overallSuccess && executionResult.CompletedSteps == executionResult.TotalSteps)
            {
                executionResult.FinalStatus = "success";
                executionResult.FinalOutput = "All steps completed successfully";
            }
            else if (executionResult.CompletedSteps > 0)
            {
                executionResult.FinalStatus = "partial_success";
                executionResult.FinalOutput = $"Completed {executionResult.CompletedSteps} of {executionResult.TotalSteps} steps";
            }
            else
            {
                executionResult.FinalStatus = "failed";
                executionResult.FinalOutput = "No steps completed successfully";
            }

            await _policyDataService.UpdateExecutionResultAsync(executionResult, cancellationToken);
            await _policyDataService.MarkCommandCompletedAsync(command.ExecutionId, cancellationToken);

            _logger.LogInformation("Policy execution completed: {ExecutionId}, Status: {Status}, Steps: {Completed}/{Total}", 
                command.ExecutionId, executionResult.FinalStatus, executionResult.CompletedSteps, executionResult.TotalSteps);
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error during policy execution {ExecutionId}", command.ExecutionId);
            await RecordExecutionFailureAsync(command, ex.Message, cancellationToken);
        }
    }

    /// <summary>
    /// Execute a single policy step
    /// </summary>
    private async Task<PolicyStepResult> ExecuteStepAsync(PolicyExecutionStep step, CancellationToken cancellationToken)
    {
        var stopwatch = Stopwatch.StartNew();
        var stepResult = new PolicyStepResult
        {
            StepNumber = step.StepNumber,
            ScriptId = step.ScriptId,
            ScriptName = step.ScriptName,
            Timestamp = DateTime.UtcNow
        };

        try
        {
            _logger.LogInformation("Executing step {StepNumber}: {ScriptName} ({ScriptType})", 
                step.StepNumber, step.ScriptName, step.ScriptType);

            var executionRequest = new ScriptExecutionRequest
            {
                ScriptType = step.ScriptType,
                ScriptContent = step.ScriptContent,
                TimeoutSeconds = step.TimeoutSeconds,
                Parameters = step.Parameters ?? new Dictionary<string, object>()
            };

            var result = await _scriptExecutor.ExecuteScriptAsync(executionRequest, cancellationToken);

            stepResult.Status = result.Success ? "success" : "failed";
            stepResult.ExitCode = result.ExitCode;
            stepResult.Output = result.Output;
            stepResult.ErrorMessage = result.Error;
            stepResult.ExecutionTimeMs = stopwatch.ElapsedMilliseconds;

            _logger.LogInformation("Step {StepNumber} completed with status: {Status}, Exit Code: {ExitCode}", 
                step.StepNumber, stepResult.Status, stepResult.ExitCode);
        }
        catch (OperationCanceledException) when (cancellationToken.IsCancellationRequested)
        {
            stepResult.Status = "cancelled";
            stepResult.ErrorMessage = "Execution was cancelled";
            stepResult.ExecutionTimeMs = stopwatch.ElapsedMilliseconds;
        }
        catch (Exception ex)
        {
            stepResult.Status = "failed";
            stepResult.ErrorMessage = ex.Message;
            stepResult.ExecutionTimeMs = stopwatch.ElapsedMilliseconds;

            _logger.LogError(ex, "Step {StepNumber} execution failed", step.StepNumber);
        }

        return stepResult;
    }

    /// <summary>
    /// Report execution results back to Satellite API
    /// </summary>
    private async Task ReportExecutionResultsAsync(CancellationToken cancellationToken)
    {
        try
        {
            var unreportedResults = await _policyDataService.GetUnreportedExecutionResultsAsync(cancellationToken);

            foreach (var result in unreportedResults)
            {
                if (cancellationToken.IsCancellationRequested)
                    break;

                await SendExecutionResultAsync(result, cancellationToken);
            }
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error reporting execution results");
        }
    }

    /// <summary>
    /// Send execution result to Satellite API
    /// </summary>
    private async Task SendExecutionResultAsync(PolicyExecutionResult result, CancellationToken cancellationToken)
    {
        try
        {
            var satelliteUrl = Environment.GetEnvironmentVariable("SATELLITE_BASE_URL") ?? "https://localhost:7200";
            var requestUri = $"{satelliteUrl}/api/policy/execution/result";

            var json = JsonSerializer.Serialize(result, new JsonSerializerOptions
            {
                PropertyNamingPolicy = JsonNamingPolicy.CamelCase
            });

            var request = new HttpRequestMessage(HttpMethod.Post, requestUri)
            {
                Content = new StringContent(json, System.Text.Encoding.UTF8, "application/json")
            };

            if (!string.IsNullOrEmpty(_registrationService.Jwt))
            {
                request.Headers.Authorization = new System.Net.Http.Headers.AuthenticationHeaderValue("Bearer", _registrationService.Jwt);
            }

            using var response = await _httpClient.SendAsync(request, cancellationToken);
            if (response.IsSuccessStatusCode)
            {
                await _policyDataService.MarkExecutionResultReportedAsync(result.ExecutionId, cancellationToken);
                _logger.LogInformation("Successfully reported execution result for {ExecutionId}", result.ExecutionId);
            }
            else
            {
                _logger.LogWarning("Failed to report execution result for {ExecutionId}: {StatusCode}", 
                    result.ExecutionId, response.StatusCode);
            }
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error sending execution result for {ExecutionId}", result.ExecutionId);
        }
    }

    /// <summary>
    /// Acknowledge command receipt
    /// </summary>
    private async Task AcknowledgeCommandAsync(string executionId, CancellationToken cancellationToken)
    {
        try
        {
            var satelliteUrl = Environment.GetEnvironmentVariable("SATELLITE_BASE_URL") ?? "https://localhost:7200";
            var requestUri = $"{satelliteUrl}/api/policy/agent/{_registrationService.AgentId}/commands/{executionId}/acknowledge";

            var request = new HttpRequestMessage(HttpMethod.Post, requestUri);
            if (!string.IsNullOrEmpty(_registrationService.Jwt))
            {
                request.Headers.Authorization = new System.Net.Http.Headers.AuthenticationHeaderValue("Bearer", _registrationService.Jwt);
            }

            using var response = await _httpClient.SendAsync(request, cancellationToken);
            if (response.IsSuccessStatusCode)
            {
                _logger.LogDebug("Acknowledged command {ExecutionId}", executionId);
            }
        }
        catch (Exception ex)
        {
            _logger.LogError(ex, "Error acknowledging command {ExecutionId}", executionId);
        }
    }

    /// <summary>
    /// Record execution failure
    /// </summary>
    private async Task RecordExecutionFailureAsync(PolicyExecutionCommand command, string errorMessage, CancellationToken cancellationToken)
    {
        var executionResult = new PolicyExecutionResult
        {
            ExecutionId = command.ExecutionId,
            AgentId = command.AgentId,
            PolicyId = command.PolicyId,
            Status = "failed",
            FinalStatus = "failed",
            ErrorSummary = errorMessage,
            TotalSteps = command.ExecutionSteps.Count,
            CompletedSteps = 0,
            StartedAt = DateTime.UtcNow,
            CompletedAt = DateTime.UtcNow,
            AgentVersion = GetAgentVersion(),
            OperatingSystem = Environment.OSVersion.Platform.ToString(),
            OsVersion = Environment.OSVersion.VersionString
        };

        await _policyDataService.StoreExecutionResultAsync(executionResult, cancellationToken);
        await _policyDataService.MarkCommandCompletedAsync(command.ExecutionId, cancellationToken);
    }

    private string GetAgentVersion()
    {
        return System.Reflection.Assembly.GetExecutingAssembly().GetName().Version?.ToString() ?? "Unknown";
    }

    public override void Dispose()
    {
        _httpClient?.Dispose();
        base.Dispose();
    }
}

#region Data Models

/// <summary>
/// Policy execution command received from Satellite API
/// </summary>
public class PolicyExecutionCommand
{
    public string ExecutionId { get; set; } = string.Empty;
    public string AgentId { get; set; } = string.Empty;
    public int PolicyId { get; set; }
    public string PolicyName { get; set; } = string.Empty;
    public List<PolicyExecutionStep> ExecutionSteps { get; set; } = new();
    public int TimeoutSeconds { get; set; } = 1800;
    public string TriggerType { get; set; } = "manual";
    public int? TriggeredBy { get; set; }
    public DateTime IssuedAt { get; set; } = DateTime.UtcNow;
    public DateTime ExpiresAt { get; set; } = DateTime.UtcNow.AddHours(24);
    public Dictionary<string, object>? Metadata { get; set; }
}

/// <summary>
/// Individual policy execution step
/// </summary>
public class PolicyExecutionStep
{
    public int StepNumber { get; set; }
    public int ScriptId { get; set; }
    public string ScriptName { get; set; } = string.Empty;
